/// <returns>An integer value.</returns>
public int active()
{
    int ret = super();

    productRecId = RetailCommerceValidatorLogContext.ProductRefRecId;
    catalogRecId = RetailCommerceValidatorLogContext.CatalogRefRecId;
    categoryRecId = RetailCommerceValidatorLogContext.CategoryRefRecId;
    genericRecId =  RetailCommerceValidatorLogContext.OMOperatingUnitId;
    hierarchyRecId = RetailCommerceValidatorLogContext.CategoryHierarchyRefRecId;
    AttributeRecId = RetailCommerceValidatorLogContext.AttributeRefRecId;
    attributeGrpRecId = RetailCommerceValidatorLogContext.AttributeGroupRefRecId;

    RetailCommerceValidatorLogContextResults_ds.executeQuery();

    return ret;
}

