/// <summary>
///  Initializes datasource
/// </summary>
public void init()
{
    super();

    QueryBuildDataSource qbdsValidatorLogContext = this.query().dataSourceName('RetailCommerceValidatorLogContextResults');
}

/// <summary>
/// Executes the data source.
/// </summary>
public void executeQuery()
{
    QueryBuildDataSource qbdsResultsContext = this.query().dataSourceName('RetailCommerceValidatorLogContextResults');
    qbdsResultsContext.clearLinks();
    qbdsResultsContext.clearRanges();

    qbdsResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,ValidationId)).value(SysQuery::value(validationId));
    qbdsResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,BatchJobId)).value(SysQuery::value(currentBatchJob));

    switch(EntityType.selection())
    {
        case RetailCommerceValidatorTestStage::Generic:
            qbrGenericRecId = qbdsResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,OMOperatingUnitId));
            qbrGenericRecId.value(SysQuery::value(genericRecId));
            break;
        case RetailCommerceValidatorTestStage::Products:
        case RetailCommerceValidatorTestStage::ProductVariants:
        case RetailCommerceValidatorTestStage::ProductKits:
            qbRangeProductId = qbdsResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,ProductRefRecId));
            qbRangeProductId.value(SysQuery::value(productRecId));
            break;
        case RetailCommerceValidatorTestStage::CommerceCategories:
            qbRangeCategoryRecId = qbdsResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,CategoryRefRecId));
            qbRangeCategoryRecId.value(SysQuery::value(categoryRecId));
            break;
        case RetailCommerceValidatorTestStage::CommerceCatalogs:
            qbRangeCatalogRecId = qbdsResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,CatalogRefRecId));
            qbRangeCatalogRecId.value(SysQuery::value(catalogRecId));
            break;
        case RetailCommerceValidatorTestStage::CommerceChannels:
            qbrGenericRecId = qbdsResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,OMOperatingUnitId));
            qbrGenericRecId.value(SysQuery::value(genericRecId));
            break;
        case RetailCommerceValidatorTestStage::CategoryHierarchies:
            qbrHierarchyRecId = qbdsResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,CategoryHierarchyRefRecId));
            qbrHierarchyRecId.value(SysQuery::value(hierarchyRecId));
            break;
        case RetailCommerceValidatorTestStage::CommerceAttributes:
            qbrAttributeRecId = qbdsResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,AttributeRefRecId));
            qbrAttributeRecId.value(SysQuery::value(AttributeRecId));
            break;
        case RetailCommerceValidatorTestStage::CommerceAttributeGroups:
            qbrAttributeGrpRecId = qbdsResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,AttributeGroupRefRecId));
            qbrAttributeGrpRecId.value(SysQuery::value(attributeGrpRecId));
            break;

    }

    QueryBuildDataSource qbdsLogs = this.query().dataSourceTable(tableNum(RetailCommerceValidatorLog));

    qbdsLogs.clearRanges();

    QueryBuildRange qbrLogType = qbdsLogs.addRange(fieldNum(RetailCommerceValidatorLog,LogType));

    switch(ResultStatus.selection())
    {
        case RetailCommerceValidatorResultStatusFilter::Warning:
            qbrLogType.value(SysQuery::value(RetailCommerceValidatorLogType::Warning));
            break;
        case RetailCommerceValidatorResultStatusFilter::Error:
            qbrLogType.value(SysQuery::value(RetailCommerceValidatorLogType::Error));
            break;
        case RetailCommerceValidatorResultStatusFilter::All:
            qbrLogType.value(SysQuery::valueNot(RetailCommerceValidatorLogType::Info));
            break;
    }

    // If there are no records fetched for RetailCommerceValidatorLogContext data source, then fetch results accordingly
    if (!RetailCommerceValidatorLogContext_ds.numberOfRowsLoaded())
    {
        element.applyResetContextResultRanges(qbdsResultsContext);
    }

    super();
}

/// <summary>
/// Redirects to the rule mitigation form.
/// </summary>
public void jumpRef()
{
    DataAreaId legalEntity = RetailCommerceValidator::getChannelLegalEntity(RetailCommerceValidatorLogContextResults.ValidationId);

    if (legalEntity && (curExt() != legalEntity))
    {
        SysInfoLogLevel prevLevel = infolog.infologLevel();
        infolog.setInfoLogLevel(SysInfoLogLevel::None);
        changecompany (legalEntity)
        {
            infolog.setInfoLogLevel(prevLevel);
            RetailCommerceValidatorRules::openRuleMitigationForm(RetailCommerceValidatorLog.RuleId, RetailCommerceValidatorLogContextResults, element.args().caller());
            infolog.setInfoLogLevel(SysInfoLogLevel::None);
        }
        infolog.setInfoLogLevel(prevLevel);
        infolog.clear();
    }
    else
    {
        RetailCommerceValidatorRules::openRuleMitigationForm(RetailCommerceValidatorLog.RuleId, RetailCommerceValidatorLogContextResults, element.args().caller());
    }
}

/// <summary>
/// Redirects to the rules.
/// </summary>
public void jumpRef()
{
    // Not providing jumpRef for RuleId.
}

