/// <summary>
/// Executes query
/// </summary>
public void executeQuery()
{
    QueryBuildDataSource qbdsPassedProducts = this.query().dataSourceName('RetailCommerceValidatorPassedProducts');

    qbdsPassedProducts.clearRanges();

    QueryBuildRange qbrVariantRange = qbdsPassedProducts.findRange(fieldNum(RetailCommerceValidatorPassedProducts,VariantRefRecId));
    QueryBuildRange qbrKitRange = qbdsPassedProducts.findRange(fieldNum(RetailCommerceValidatorPassedProducts,KitRefRecId));

    qbdsPassedProducts.addRange(fieldNum(RetailCommerceValidatorPassedProducts, ValidationId)).value(SysQuery::value(validationId));
    qbdsPassedProducts.addRange(fieldNum(RetailCommerceValidatorPassedProducts, BatchJobId)).value(SysQuery::value(currentBatchJob));

    if (!qbrVariantRange)
    {
        qbrVariantRange = qbdsPassedProducts.addRange(fieldNum(RetailCommerceValidatorPassedProducts,VariantRefRecId));
    }

    if (!qbrKitRange)
    {
        qbrKitRange = qbdsPassedProducts.addRange(fieldNum(RetailCommerceValidatorPassedProducts,KitRefRecId));
    }

    switch(EntityType.selection())
    {
        case RetailCommerceValidatorTestStage::Products:
            qbrVariantRange.value(SysQuery::value(0));
            qbrKitRange.value(SysQuery::value(0));
            break;
        case RetailCommerceValidatorTestStage::ProductVariants:
            qbrVariantRange.value(SysQuery::valueNot(0));
            qbrKitRange.value(SysQuery::value(0));
            break;
        case RetailCommerceValidatorTestStage::ProductKits:
            qbrKitRange.value(SysQuery::valueNot(0));
            break;
    }

    super();
}

