    qbdsPassedCatalogCounter.addRange(fieldNum(RetailCommerceValidatorLogCountersByContext, EntityType)).value(SysQuery::value(RetailCommerceValidatorTestStage::CommerceCatalogs));
    qbdsPassedCatalogCounter.addRange(fieldNum(RetailCommerceValidatorLogCountersByContext, IsTemp)).value(SysQuery::value(NoYes::No));
    qbdsPassedCatalogCounter.addRange(fieldNum(RetailCommerceValidatorLogCountersByContext, MessageCount)).value(SysQuery::valueNot(0));

    qbdsPassedCatalogCounter.addGroupByField(fieldNum(RetailCommerceValidatorLogCountersByContext, EntityRefRecId));
    qbdsPassedCatalogCounter.addSelectionField(fieldNum(RetailCommerceValidatorLogCountersByContext, MessageCount), SelectionField::Max);
}

