public void init()
{
    super();

    QueryBuildDataSource qbdsCommerceValidator = this.query().dataSourceName('RetailCommerceValidatorPassed');

    qbdsCommerceValidator.addLink(fieldNum(RetailChannelTable, OMOperatingUnitID), fieldNum(RetailCommerceValidator, OMOperatingUnitId), 'RetailChannelTablePassed');
    qbdsCommerceValidator.addRange(fieldnum(RetailCommerceValidator, ValidationId)).value(SysQuery::value(validationId));

    QueryBuildDataSource qbdsLogContext = qbdsCommerceValidator.addDataSource(tableNum(RetailCommerceValidatorLogContext));

    qbdsLogContext.addLink(fieldNum(RetailCommerceValidator, ValidationId), fieldNum(RetailCommerceValidatorLogContext, ValidationId));
    qbdsLogContext.addLink(fieldNum(RetailCommerceValidator, OMOperatingUnitId), fieldNum(RetailCommerceValidatorLogContext, OMOperatingUnitId));
    qbdsLogContext.addLink(fieldNum(RetailCommerceValidator, CurrentbatchJobId), fieldNum(RetailCommerceValidatorLogContext, BatchJobId));
    qbdsLogContext.joinMode(JoinMode::NoExistsJoin);

    qbdsLogContext.addRange(fieldNum(RetailCommerceValidatorLogContext, BatchJobId)).value(SysQuery::value(currentBatchJob));
}

