public void executeQuery()
{
    QueryBuildDataSource qbdsPassedResultsContext = this.query().dataSourceName('RetailCommerceValidatorLogContextPassedResults');
    qbdsPassedResultsContext.clearLinks();
    qbdsPassedResultsContext.clearRanges();

    qbdsPassedResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,ValidationId)).value(SysQuery::value(validationId));
    qbdsPassedResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,BatchJobId)).value(SysQuery::value(currentBatchJob));

    switch(EntityType.selection())
    {
        case RetailCommerceValidatorTestStage::Generic:
        case RetailCommerceValidatorTestStage::CommerceChannels:
            QueryBuildRange qbrChannelRecId = qbdsPassedResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,OMOperatingUnitId));
            qbrChannelRecId.value(SysQuery::value(passedChannelRecId));
            break;
        case RetailCommerceValidatorTestStage::Products:
        case RetailCommerceValidatorTestStage::ProductVariants:
        case RetailCommerceValidatorTestStage::ProductKits:
            QueryBuildRange qbrProductPassed = qbdsPassedResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,ProductRefRecId));
            qbrProductPassed.value(SysQuery::value(passedProductRecId));
            break;
        case RetailCommerceValidatorTestStage::CommerceCategories:
            qbRangeCategoryRecId = qbdsPassedResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,CategoryRefRecId));
            qbRangeCategoryRecId.value(SysQuery::value(passedCategoryRecId));
            break;
        case RetailCommerceValidatorTestStage::CommerceCatalogs:
            qbRangeCatalogRecId = qbdsPassedResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,CatalogRefRecId));
            qbRangeCatalogRecId.value(SysQuery::value(passedCatalogRecId));
            break;
        case RetailCommerceValidatorTestStage::CategoryHierarchies:
            qbrHierarchyRecId = qbdsPassedResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,CategoryHierarchyRefRecId));
            qbrHierarchyRecId.value(SysQuery::value(passedCategoryHierarchyRecId));
            break;
        case RetailCommerceValidatorTestStage::CommerceAttributes:
            qbrAttributeRecId = qbdsPassedResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,AttributeRefRecId));
            qbrAttributeRecId.value(SysQuery::value(passedAttributeRecId));
            break;
        case RetailCommerceValidatorTestStage::CommerceAttributeGroups:
            qbrAttributeGrpRecId = qbdsPassedResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,AttributeGroupRefRecId));
            qbrAttributeGrpRecId.value(SysQuery::value(passedAttributeGrpRecId));
            break;
    }

    if (element.canResetRangesIfPassedEntitiesGridIsEmpty(qbdsPassedResultsContext))
    {
        element.applyResetContextResultRanges(RetailCommerceValidatorLogContextPassedResults_DS.queryBuildDataSource());
    }

    super();
}

/// <summary>
/// Redirects to the rule mitigation form.
/// </summary>
public void jumpRef()
{
    DataAreaId legalEntity = RetailCommerceValidator::getChannelLegalEntity(RetailCommerceValidatorLogContextPassedResults.ValidationId);

    if (legalEntity && (curExt() != legalEntity))
    {
        SysInfoLogLevel prevLevel = infolog.infologLevel();
        infolog.setInfoLogLevel(SysInfoLogLevel::None);

        changecompany (legalEntity)
        {
            infolog.setInfoLogLevel(prevLevel);
            RetailCommerceValidatorRules::openRuleMitigationForm(RetailCommerceValidatorLogPassed.RuleId, RetailCommerceValidatorLogContextPassedResults, element.args().caller());
            infolog.setInfoLogLevel(SysInfoLogLevel::None);
        }

        infolog.setInfoLogLevel(prevLevel);
        infolog.clear();
    }
    else
    {
        RetailCommerceValidatorRules::openRuleMitigationForm(RetailCommerceValidatorLogPassed.RuleId, RetailCommerceValidatorLogContextPassedResults, element.args().caller());
    }
}

/// <summary>
/// Initializes the data source.
/// </summary>
public void init()
{
    super();

    QueryBuildDataSource qbdsPassedLogs = this.query().dataSourceName('RetailCommerceValidatorLogPassed');

    qbdsPassedLogs.addRange(fieldNum(RetailCommerceValidatorLog, LogType)).value(SysQuery::value(RetailCommerceValidatorLogType::Info));
}

public void jumpRef()
{
    // Not providing jumpRef for RuleId.
}

/// <summary>
/// Updates the data in the grid acorrding to selection.
/// </summary>
/// <returns>The selected value.</returns>
public int selectionChange()
{
    int ret = super();

    switch(EntityType.selection())
    {
        case RetailCommerceValidatorTestStage::Generic:
            element.setGridFieldsVisibility(false, false, false, true);
            element.prepareGenericQuery();
            break;
        case RetailCommerceValidatorTestStage::Products:
            element.setGridFieldsVisibility(true);
            element.prepareStandaloneAndMasterProductsQuery();
            element.prepareEcoResProductQuery();
            break;
        case RetailCommerceValidatorTestStage::ProductVariants:
            element.setGridFieldsVisibility(true);
            element.prepareVariantProductsQuery();
            element.prepareEcoResProductQuery();
            break;
        case RetailCommerceValidatorTestStage::ProductKits:
            element.setGridFieldsVisibility(true);
            element.prepareKitProductsQuery();
            element.prepareEcoResProductQuery();
            break;
        case RetailCommerceValidatorTestStage::CommerceCatalogs:
            element.setGridFieldsVisibility(false, true);
            element.prepareCatalogsQuery();
            break;
        case RetailCommerceValidatorTestStage::CommerceCategories:
            element.setGridFieldsVisibility(false, false, true);
            element.prepareCategoryQuery();
            break;
        case RetailCommerceValidatorTestStage::CategoryHierarchies:
            element.setGridFieldsVisibility(false, false,false,false, true);
            element.prepareCategoryHierarchyQuery();
            break;
        case RetailCommerceValidatorTestStage::CommerceAttributes:
            element.setGridFieldsVisibility(false, false,false,false, false,true);
            element.prepareAttributeQuery();
            break;
        case RetailCommerceValidatorTestStage::CommerceAttributeGroups:
            element.setGridFieldsVisibility(false, false,false,false, false,false,true);
            element.prepareAttributeGroupQuery();
            break;
        case RetailCommerceValidatorTestStage::CommerceChannels:
            element.setGridFieldsVisibility(false, false, false, false, false, false, false, true);
            element.prepareChannelQuery();
            break;
        default:
            // Show products as deafult.
            element.setGridFieldsVisibility(true, false, false);
            element.prepareStandaloneAndMasterProductsQuery();
            element.prepareEcoResProductQuery();
            break;
    }

    element.prepareLogCounterByContextQuery();
    element.resetRangeValues();

    RetailCommerceValidatorLogContext_ds.executeQuery();
    RetailCommerceValidatorLogContextResults_ds.executeQuery();

    element.showPassedEntities(PassedEntities.visible());

    return ret;
}

/// <summary>
/// Updates the data in the results grid acorrding to selection.
/// </summary>
/// <returns>The selected value.</returns>
public int selectionChange()
{
    int ret;

    ret = super();

    RetailCommerceValidatorLogContextResults_ds.executeQuery();

    return ret;
}

public void enter()
{
    super();

    this.delete(enum2Str(RetailCommerceValidatorResultStatusFilter::Message));
}
