Method.Source:
    [Form]
    public class RetailConfiguration_BR extends FormRun
    {
        boolean             configByStore;
}

public void init()
{
    super();

    //Initialize splitter

    StoreConfigurationSelector.clear();
    StoreConfigurationSelector.add("@SYS4007464");
    StoreConfigurationSelector.add("@RET3005");
    StoreConfigurationSelector.selection(0);

    configByStore = false;
}

/// <summary>
/// Updates the design of the form for configuring by "fiscal printer configurations" or "stores".
/// </summary>
private void updateDesign()
{
    str leftEntity, middleRightEntity;

    // configByStore=true  -> Left=Stores, Middle=Available configurations, Right=Selected configurations
    // configByStore=false -> Left=Configurations, Middle=Available stores, Right=Selected stores
    if (configByStore)
    {
        leftEntity          = "@RET3005";
        middleRightEntity   = "@SYS4007464";
    }
    else
    {
        leftEntity          = "@SYS4007464";
        middleRightEntity   = "@RET3005";
    }

    this.design().caption(strFmt("@SYS4007458", leftEntity, middleRightEntity));

    // Left pane
    //   if configByStore is true, show only the Stores grid
    //   else, show the Configurations grid and type selector
    StoreConfigurationSectionText.text(strFmt("@SYS4007459", leftEntity));

    Left_ConfigTypeSelector.visible(!configByStore);

    LeftConfigsActionPane.visible(!configByStore);
    LeftConfigsGrid.visible(!configByStore);

    LeftStoresGrid.visible(configByStore);

    // Middle pane (available entities)
    //   if configByStore is true, show the Configurations grid and type selector
    //   else, show only the Stores grid
    AvailableGroup.caption(strFmt("@SYS4007460", middleRightEntity));
    AvailableText.text(strFmt("@SYS4007462", middleRightEntity, middleRightEntity));

    Middle_ConfigTypeSelector.visible(configByStore);

    MiddleStoresActionPane.visible(!configByStore);
    MiddleStoresGrid.visible(!configByStore);

    MiddleConfigsActionPane.visible(configByStore);
    MiddleConfigsGrid.visible(configByStore);

    // Right pane (selected entities)
    //   if configByStore is true, show the Configurations grid
    //   else, show the Stores grid
    SelectedGroup.caption(strFmt("@SYS4007461", middleRightEntity));
    SelectedText.text(strFmt("@SYS4007463", middleRightEntity, middleRightEntity));

    RightStoresActionPane.visible(!configByStore);
    RightStoresGrid.visible(!configByStore);

    RightConfigsActionPane.visible(configByStore);
    RightConfigsGrid.visible(configByStore);
}

public int active()
{
    QueryBuildDataSource    qbdsAvailable, qbdsSelected;
    int                     ret;

    ret = super();

    if (configByStore)
    {
        qbdsAvailable = RetailConfigurationAvailable_ds.query().dataSourceName(RetailConfigStoreAvailable_ds.name());
        qbdsAvailable.clearRange(fieldNum(RetailConfigurationStoreTable_BR, RetailStoreTable));
        qbdsAvailable.addRange(fieldNum(RetailConfigurationStoreTable_BR, RetailStoreTable)).value(queryValue(RetailStoreTable.RecId));

        RetailConfigurationAvailable_ds.executeQuery();

        qbdsSelected = RetailConfigurationSelected_ds.query().dataSourceName(RetailConfigStoreSelected_ds.name());
        qbdsSelected.clearRange(fieldNum(RetailConfigurationStoreTable_BR, RetailStoreTable));
        qbdsSelected.addRange(fieldNum(RetailConfigurationStoreTable_BR, RetailStoreTable)).value(queryValue(RetailStoreTable.RecId));

        RetailConfigurationSelected_ds.executeQuery();
    }

    return ret;
}

public int active()
{
    QueryBuildDataSource    qbdsAvailable, qbdsSelected;
    int                     ret;

    ret = super();

    if (!configByStore)
    {
        qbdsAvailable = RetailStoreTableAvailable_ds.query().dataSourceName(RetailStoreConfigAvailable_ds.name());
        qbdsAvailable.clearRange(fieldNum(RetailConfigurationStoreTable_BR, RetailConfiguration_BR));
        qbdsAvailable.addRange(fieldNum(RetailConfigurationStoreTable_BR, RetailConfiguration_BR)).value(queryValue(RetailConfiguration_BR.RecId));

        RetailStoreTableAvailable_ds.executeQuery();

        qbdsSelected = RetailStoreTableSelected_ds.query().dataSourceName(RetailStoreConfigSelected_ds.name());
        qbdsSelected.clearRange(fieldNum(RetailConfigurationStoreTable_BR, RetailConfiguration_BR));
        qbdsSelected.addRange(fieldNum(RetailConfigurationStoreTable_BR, RetailConfiguration_BR)).value(queryValue(RetailConfiguration_BR.RecId));

        RetailStoreTableSelected_ds.executeQuery();
    }

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    configByStore = StoreConfigurationSelector.selection();
    element.run();

    return ret;
}

public boolean modified()
{
    QueryBuildDataSource    qbds;
    boolean                 ret;

    ret = super();

    qbds = RetailConfiguration_BR_ds.query().dataSourceName(RetailConfiguration_BR_ds.name());
    qbds.clearRange(fieldNum(RetailConfiguration_BR, Type));

    if (this.selection() != RetailConfigurationType_BR::None)
    {
        qbds.addRange(fieldNum(RetailConfiguration_BR, Type)).value(queryValue(this.selection()));
    }

    RetailConfiguration_BR_ds.executeQuery();

    return ret;
}

public boolean leave()
{
    boolean ret;

    ret = super();

    RetailConfiguration_BR.keepValidTaxRateConfigurationValue();

    return ret;
}

public boolean modified()
{
    QueryBuildDataSource    qbdsAvailable, qbdsSelected;
    boolean                 ret;

    ret = super();

    qbdsAvailable = RetailConfigurationAvailable_ds.query().dataSourceName(RetailConfigurationAvailable_ds.name());
    qbdsAvailable.clearRange(fieldNum(RetailConfiguration_BR, Type));

    qbdsSelected = RetailConfigurationSelected_ds.query().dataSourceName(RetailConfigurationSelected_ds.name());
    qbdsSelected.clearRange(fieldNum(RetailConfiguration_BR, Type));

    if (this.selection() != RetailConfigurationType_BR::None)
    {
        qbdsAvailable.addRange(fieldNum(RetailConfiguration_BR, Type)).value(queryValue(this.selection()));
        qbdsSelected.addRange(fieldNum(RetailConfiguration_BR, Type)).value(queryValue(this.selection()));
    }

    RetailConfigurationAvailable_ds.executeQuery();
    RetailConfigurationSelected_ds.executeQuery();

    return ret;
}

void clicked()
{
    RetailConfiguration_BR              configuration;
    RetailConfigurationStoreTable_BR    configurationStore;
    MultiSelectionHelper                helper = MultiSelectionHelper::construct();

    super();

    helper.parmDatasource(RetailConfigurationAvailable_ds);

    configuration = helper.getFirst();

    while (configuration.RecId != 0)
    {
        configurationStore.clear();

        configurationStore.RetailStoreTable         = RetailStoreTable.RecId;
        configurationStore.RetailConfiguration_BR   = configuration.RecId;

        configurationStore.insert();

        configuration = helper.getNext();
    }

    RetailConfigurationAvailable_ds.research();
    RetailConfigurationSelected_ds.research();
}

void clicked()
{
    RetailStoreTable                    store;
    RetailConfigurationStoreTable_BR    configurationStore;
    MultiSelectionHelper                helper = MultiSelectionHelper::construct();

    super();

    helper.parmDatasource(RetailStoreTableAvailable_ds);

    store = helper.getFirst();

    while (store.RecId != 0)
    {
        configurationStore.clear();

        configurationStore.RetailStoreTable         = store.RecId;
        configurationStore.RetailConfiguration_BR   = RetailConfiguration_BR.RecId;

        configurationStore.insert();

        store = helper.getNext();
    }

    RetailStoreTableAvailable_ds.research();
    RetailStoreTableSelected_ds.research();
}

void clicked()
{
    RetailConfiguration_BR              configuration;
    RetailConfigurationStoreTable_BR    configurationStore;
    MultiSelectionHelper                helper = MultiSelectionHelper::construct();

    super();

    helper.parmDatasource(RetailConfigurationSelected_ds);

    configuration = helper.getFirst();

    while (configuration.RecId != 0)
    {
        delete_from configurationStore
            where   configurationStore.RetailStoreTable         == RetailStoreTable.RecId
                &&  configurationStore.RetailConfiguration_BR   == configuration.RecId;

        configuration = helper.getNext();
    }

    RetailConfigurationAvailable_ds.research();
    RetailConfigurationSelected_ds.research();
}

