/// <summary>
/// Validates to check existing Channel table name.
/// </summary>
/// <returns>
/// true if the record is valid; otherwise, false.
/// </returns>
public boolean validateWrite()
{
    boolean ret;
    RetailConnSchedulerSubjobTable schedulerSubjobTable;

    ret = super();

    select firstonly RecId, subJobId from schedulerSubjobTable
        where schedulerSubjobTable.ChannelTableName == RetailConnSchedulerSubjobTable.ChannelTableName &&
            schedulerSubjobTable.RetailConnChannelSchema == RetailConnSchedulerSubjobTable.RetailConnChannelSchema &&
            schedulerSubjobTable.RecId != RetailConnSchedulerSubjobTable.RecId;

    if (schedulerSubjobTable.RecId)
    {
        checkFailed(strFmt("@Retail:RetailCDXSubjobDuplicateChannelTable", RetailConnSchedulerSubjobTable.subJobId, schedulerSubjobTable.subJobId, RetailConnSchedulerSubjobTable.ChannelTableName));

        // if a new record is being created, fail validation (updates won't be blocked)
        if (!RetailConnSchedulerSubjobTable.RecId)
        {
            ret = false;
        }
    }

    return ret;
}

