public void lookup()
{
    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource;
    QueryBuildRange queryBuildRange;
    SysTableLookup sysTableLookup;
    ;

    if (RetailConnSchedulerSubjobTable.RetailConnChannelSchema)
    {
        sysTableLookup = SysTableLookup::newParameters(tableNum(RetailConnLocationDesignTable), this);

        sysTableLookup.addLookupField(fieldNum(RetailConnLocationDesignTable, locationTableName));

        queryBuildDataSource = query.addDataSource(tableNum(RetailConnLocationDesignTable));

        queryBuildRange = queryBuildDataSource.addRange(fieldNum(RetailConnLocationDesignTable, RetailConnChannelSchema));
        queryBuildRange.value(int642str(RetailConnSchedulerSubjobTable.RetailConnChannelSchema));

        sysTableLookup.parmQuery(query);
        sysTableLookup.performFormLookup();
    }
    else
    {
        super();
    }
}

