/// <summary>
/// Provides lookup with the list of fields of the table specified in <c>FromTableName</c> field.
/// </summary>
public void lookup()
{
    int tableid;
    int fieldid;

    Args    args;
    FormRun  formRun;
    RetailConnLocationDesignTable designTable;

    if (RetailConnSchedulerSubjobTable.dataReplicationByPull)
    {
        designTable = RetailConnLocationDesignTable::find(RetailConnSchedulerSubjobTable.RetailConnChannelSchema, RetailConnSchedulerSubjobTable.ChannelTableName);

        if (designTable)
        {
            args = new Args();
            args.name(formstr(RetailConnFieldNameLookup));
            args.caller(this);
            args.record(designTable);

            formRun = classfactory.formRunClass(args);
            formRun.init();
            this.performFormLookup(formRun);
        }
        else
        {
            super();
        }
    }
    else
    {
        tableid = tablename2id(RetailConnSchedulerSubjobTable.AXTableName);
        fieldid = pickField(tableid);
        RetailConnSchedulerSubjobFieldList.FromFieldName = fieldid2name(tableid, fieldid);
        RetailConnSchedulerSubjobFieldList_ds.refresh();
    }
}

