public void close()
{
    if (numberSeqOperatingUnitNumber)
    {
        numberSeqOperatingUnitNumber.formMethodClose();
    }

    if (numberSeqStoreId)
    {
        numberSeqStoreId.formMethodClose();
    }

    super();
}

public void init()
{
    super();

    customerAddressBookCtrl = SysLookupMultiSelectCtrl::construct(element, CustomerAddressBook, queryStr(DirAddressBookLookup));
    employeeAddressBookCtrl = SysLookupMultiSelectCtrl::construct(element, EmployeeAddressBook, queryStr(DirAddressBookLookup));
    NumberSequenceTable::autoCreate(extendedTypeNum(OMOperatingUnitNumber), NumberSeqScopeFactory::createGlobalScope(), '', NoYes::No, '########');

    this.setPriceGroupsButtonVisibility();
}

private void setPriceGroupsButtonVisibility()
{
    PriceGroupsButton.visible(!RetailPricingFeatureToggle::isRetailPricingPropertyFeatureEnabled());
}

NumberSeqFormHandler numberSeqOperatingUnitNumber()
{
    if ( !numberSeqOperatingUnitNumber )
    {
        numberSeqOperatingUnitNumber = NumberSeqFormHandler::newForm(
            OMOperatingUnit::getNumberSequenceReference(OMOperatingUnitType::RetailChannel).NumberSequenceId,
            element,
            OMOperatingUnit_DS,
            fieldNum(OMOperatingUnit, OMOperatingUnitNumber));
    }
    return numberSeqOperatingUnitNumber;
}

NumberSeqFormHandler numberSeqStoreId()
{
    if (!numberSeqStoreId)
    {
        numberSeqStoreId = NumberSeqFormHandler::newForm(RetailParameters::numRefDirectSalesStoreId().NumberSequenceId,
                                                         element,
                                                         RetailDirectSalesChannel_DS,
                                                         fieldNum(RetailDirectSalesChannel, DSStoreNumber)
                                                         );
    }

    return numberSeqStoreId;
}

public void updateAddressBooks()
{
    // Customer
    element.updateRetailAddressBook(RetailAddressBookType::Customer);

    // Employe
    element.updateRetailAddressBook(RetailAddressBookType::Employee);
}

public void updateControls()
{
    if (!isWriting)
    {
        customerAddressBookCtrl.set(retailStoreAddressBook.addressBooks(RetailDirectSalesChannel.RecId, RetailAddressBookType::Customer));
        employeeAddressBookCtrl.set(retailStoreAddressBook.addressBooks(RetailDirectSalesChannel.RecId, RetailAddressBookType::Employee));
    }
    addressBookControlsStoreRecId = RetailDirectSalesChannel.RecId;
}

/// <summary>
/// Handling the currently active task.
/// </summary>
/// <param name = "_taskId"></param>
/// <returns></returns>
public int task(int _taskId)
{
    #Task

    int ret;
    ret = super(_taskId);

    if (_taskId == #taskTab)
    {
        // Buttons to show in Grid View
        EditDetailsButton.visible(TabPageGrid.isActivePage());
        ListGroup.visible(TabPageGrid.isActivePage());
    }
    return ret;
}

private void updateRetailAddressBook(RetailAddressBookType addressBookType)
{
    // Only do this if we have a RetailDirectSalesChannel and its address books were loaded
    if (RetailDirectSalesChannel.RecId && RetailDirectSalesChannel.RecId == addressBookControlsStoreRecId)
    {
        // Gettting the address from the customer addressbook container
        container addressBookContainer;
        if (addressBookType == RetailAddressBookType::Customer)
        {
            addressBookContainer = customerAddressBookCtrl.getSelectedFieldValues();
        }
        else 
        { 
            addressBookContainer = employeeAddressBookCtrl.getSelectedFieldValues();
        }
        Set selectedAddresses = new Set(Types::String);
        for (int i = 1; i <= conLen(addressBookContainer); i++)
        {
            selectedAddresses.add(conPeek(addressBookContainer,i));
        }

        // Update the addressbooks to the database
        RetailStoreAddressBook::updateAddressBooks(RetailDirectSalesChannel.RecId, selectedAddresses, addressBookType);

    }
}

