public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    SysReferenceTableLookup sysTableLookup;
    Query query;
    QueryBuildDataSource qbDS;

    sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(RetailChannelProfile), _formReferenceControl, true);
    sysTableLookup.addLookupfield(fieldNum(RetailChannelProfile, Name));

    query = new Query();
    qbDS = query.addDataSource(tableNum(RetailChannelProfile));
    qbDS.addRange(fieldNum(RetailChannelProfile, ChannelProfileType)).value(queryValue(RetailChannelProfileType::RetailServer));
    sysTableLookup.parmQuery(query);
    return sysTableLookup.performFormLookup();
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    // For setting live channel database:
    // Only show the channel databases that actually contain this channel
    SysReferenceTableLookup sysTableLookup;
    Query query;
    QueryBuildDataSource qbdsDataStore, qbdsDataStoreChannel;

    sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(RetailConnDatabaseProfile), _formReferenceControl, true);
    sysTableLookup.addLookupfield(fieldNum(RetailConnDatabaseProfile, Name));

    query = new Query();
    qbdsDataStore = query.addDataSource(tableNum(RetailConnDatabaseProfile));
    qbdsDataStoreChannel = qbdsDataStore.addDataSource(tableNum(RetailCDXDataStoreChannel), tableStr(RetailCDXDataStoreChannel));
    qbdsDataStoreChannel.relations(true);
    qbdsDataStoreChannel.joinMode(JoinMode::ExistsJoin);

    qbdsDataStoreChannel.addRange(fieldNum(RetailCDXDataStoreChannel, Channel)).value(queryValue(RetailDirectSalesChannel.RecId));
    sysTableLookup.parmQuery(query);
    return sysTableLookup.performFormLookup();
}

public void init()
{
    HcmWorker               hcmWorker;
    RetailStoreAddressBook  storeAddressBook;
    DirAddressBookParty     dirAddressBookParty;
    QueryBuildDataSource    qbds;

    super();
    RetailDirectSalesChannel_ds.object(fieldNum(RetailDirectSalesChannel, DSTaxGroup)).mandatory(true);

    if (element.args().dataset() && element.args().dataset() == tablenum(HcmWorker))
    {
        hcmWorker = element.args().record();
        qbds = this.query().dataSourceTable(tableNum(RetailDirectSalesChannel));

        while select AddressBook from dirAddressBookParty where dirAddressBookParty.Party == hcmWorker.Person
            join AddressBook, StoreRecId from storeAddressBook where storeAddressBook.AddressBookType == RetailAddressBookType::Employee
                && storeAddressBook.AddressBook == dirAddressBookParty.AddressBook
        {
            qbds.addRange(fieldNum(RetailDirectSalesChannel, RecId)).value(queryValue(storeAddressBook.StoreRecId));
        }
    }
}

public boolean leaveRecord(boolean _forceUpdate = false)
