public int active()
{
    int ret;
    RefRecId storeDataAreaRecId;
    ret = super();

    if (RetailDirectSalesChannel.inventLocationDataAreaId && RetailDirectSalesChannel.inventLocationDataAreaId != RetailDirectSalesChannel.company())
    {
        storeDataAreaRecId = CompanyInfo::findDataArea(RetailDirectSalesChannel.inventLocationDataAreaId).RecId;
        DimensionEntryControl.parmCompany(RetailDirectSalesChannel.inventLocationDataAreaId);
        DimensionEntryControl.reactivate();
    }
    else if (ret)
    {
        DimensionEntryControl.parmCompany(RetailDirectSalesChannel.company());
        DimensionEntryControl.reactivate();
    }

    RetailDirectSalesChannel_DS.object(fieldnum(RetailDirectSalesChannel, DSClosingMethod)).allowEdit(RetailDirectSalesChannel.DSstatementMethod != RetailStaffTerminalTotal::Staff);

    element.updateControls();

    return ret;
}

