        element.numberSeqStoreId().formMethodDataSourceCreate();
    }
}

void delete()
{
    ttsbegin;
    element.numberSeqStoreId().formMethodDataSourceDelete();

    super();
    ttscommit;
}

void write()
{
    boolean isInsert = RetailDirectSalesChannel.RecId == 0;
    isWriting = true;
    ttsbegin;

    // Ensure that RetailDirectSalesChannel and OMOperatingUnit are updated transactionally.
    if (isInsert)
    {
        oMOperatingUnit.insert();
    }
    else
    {
        oMOperatingUnit.update();
    }

    element.numberSeqStoreId().formMethodDataSourceWrite();

    RetailDirectSalesChannel.OMOperatingUnitID = OMOperatingUnit.RecId;
    super();

    if (isInsert && addressBookControlsStoreRecId == 0)
    {
        addressBookControlsStoreRecId = RetailDirectSalesChannel.RecId;

        // update the address books when saving the data
        // #Bug 438872 to consolidate both insert/update places into one. Now this place is only handled for insert.
        element.updateAddressBooks();
    }            

    if (!RetailChannelTableExt.Channel)
    {
        RetailChannelTableExt.Channel = RetailDirectSalesChannel.RecId;
        RetailChannelTableExt.write();
    }

    ttscommit;
    isWriting = false;
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();
    if (ret && !element.numberSeqStoreId().formMethodDataSourceValidateWrite())
    {
        ret = false;
    }

    return ret;
}

