void clicked()
{
    // overwriting the common procedure for menu button click in order to run Payment methods form in the context of the store data area
    MenuFunction menuFunction = new MenuFunction(menuitemDisplayStr(RetailStoreTenderTypeTable), MenuItemType::Display);

    Args args = new Args();
    args.caller(this);
    args.record(RetailDirectSalesChannel);

    changeCompany(RetailDirectSalesChannel.inventLocationDataAreaId)
    {
        menuFunction.run(args);
    }
}

