using System.Collections;
using System.Collections.Generic;

[Form]
public class RetailDiscountValidationPeriod extends FormRun
{
    private const str endTimeLabel = "@RetailDiscount:endTime"; // End time
    private const str endTimeWithNextDayHintLabel = "@RetailDiscount:endTimeWithNextDayHint"; // End time (Next day)

}

private void updatePeriodEndingTimeLabel()
{
    Period_endingTime.label(retailDiscountValidationPeriod.endingTimeAfterMidnight
        ? endTimeWithNextDayHintLabel
        : endTimeLabel);
}

private void updateMondayEndingTimeLabel()
{
    MondayPeriod_mondayEndingTime.label(retailDiscountValidationPeriod.mondayEndingTimeAfterMidnight
        ? endTimeWithNextDayHintLabel
        : endTimeLabel);
}

private void updateTuesdayEndingTimeLabel()
{
    ThuesdayPeriod_thuesdayEndingTime.label(retailDiscountValidationPeriod.thuesdayEndingTimeAfterMidnight
        ? endTimeWithNextDayHintLabel
        : endTimeLabel);
}

private void updateWednesdayEndingTimeLabel()
{
