}

private void updateSundayEndingTimeLabel()
{
    SundayPeriod_sundayEndingTime.label(retailDiscountValidationPeriod.sundayEndingTimeAfterMidnight
        ? endTimeWithNextDayHintLabel
        : endTimeLabel);
}

public int active()
{
    int ret;

    ret = super();

    this.updateWeekdayTabVisibility();
    this.updateDiagnosticsTabVisibility();
    this.updateEndTimeLabel();

    return ret;
}

internal edit RetailMondayBound editIsMondayEnabled(boolean _set, RetailDiscountValidationPeriod _retailDiscountValidationPeriod, NoYes _value)
{
    if (_set)
    {
        if (_value == NoYes::Yes)
        {
            Monday.visible(true);
            _retailDiscountValidationPeriod.mondayTimeWithinBounds = NoYes::Yes;
        }
        else
        {
            Monday.visible(false);
            _retailDiscountValidationPeriod.mondayStartingTime = 0;
            _retailDiscountValidationPeriod.mondayEndingTime = 0;
        }

        this.updateDiagnosticsTabVisibility();

        return _value;
    }

    return Period_isMondayEnabled.checked();
}

