internal display str getDiagnosticsString(RetailDiscountValidationPeriod _retailDiscountValidationPeriod)
{
    return RetailDiscountValidationPeriodHelper::getDiagnosticsString(_retailDiscountValidationPeriod);
}

private void updateWeekdayTabVisibility()
{
    Period_isMondayEnabled.checked(retailDiscountValidationPeriod.isMondayEnabled());
    Monday.visible(retailDiscountValidationPeriod.isMondayEnabled());

    Period_isTuesdayEnabled.checked(retailDiscountValidationPeriod.isTuesdayEnabled());
    Thuesday.visible(retailDiscountValidationPeriod.isTuesdayEnabled());

    Period_isWednesdayEnabled.checked(retailDiscountValidationPeriod.isWednesdayEnabled());
    Wednesday.visible(retailDiscountValidationPeriod.isWednesdayEnabled());

    Period_isThursdayEnabled.checked(retailDiscountValidationPeriod.isThursdayEnabled());
    Thursday.visible(retailDiscountValidationPeriod.isThursdayEnabled());

    Period_isFridayEnabled.checked(retailDiscountValidationPeriod.isFridayEnabled());
    Friday.visible(retailDiscountValidationPeriod.isFridayEnabled());

    Period_isSaturdayEnabled.checked(retailDiscountValidationPeriod.isSaturdayEnabled());
    Saturday.visible(retailDiscountValidationPeriod.isSaturdayEnabled());

    Period_isSundayEnabled.checked(retailDiscountValidationPeriod.isSundayEnabled());
    Sunday.visible(retailDiscountValidationPeriod.isSundayEnabled());
}

private void updateDiagnosticsTabVisibility()
{
    DiagnosticTab.visible(
        Period_isMondayEnabled.checked()    ||
        Period_isTuesdayEnabled.checked()   ||
        Period_isWednesdayEnabled.checked() ||
        Period_isThursdayEnabled.checked()  ||
        Period_isFridayEnabled.checked()    ||
        Period_isSaturdayEnabled.checked()  ||
        Period_isSundayEnabled.checked()
    );
}

private void updateEndTimeLabel()
{
    element.updatePeriodEndingTimeLabel();
    element.updateMondayEndingTimeLabel();
    element.updateTuesdayEndingTimeLabel();
    element.updateWednesdayEndingTimeLabel();
    element.updateThursdayEndingTimeLabel();
    element.updateFridayEndingTimeLabel();
    element.updateSaturdayEndingTimeLabel();
    element.updateSundayEndingTimeLabel();
}

