/// <summary>
/// Gets total sales amount without prepayment.
/// </summary>
/// <param name = "_source">
/// EInvoice jour for MX.
/// </param>
/// <returns>
/// Total sales amount without prepayment.
/// </returns>
public display RetailSalesAmount statementSalesTotalExclPrepayment(EInvoiceJour_MX _source)
{
    return -1 * RetailEInvoiceAggregatedStatement_MX::findRecId(_source.RefRecId).salesTotalExclPrepayment(_source.IsReturn);
}

public void init()
{
    super();
    allowEditFieldsOnFormDS_W(this, false);
}

public int active()
{
    int ret = super();

    EInvoiceJour_ds.object(fieldNum(EInvoiceJour_MX, InvoiceDateTime)).allowEdit(EInvoiceJour.CFDIStatus == EInvoiceCFDIStatus_MX::Blank);

    return ret;
}

public void init()
{
    super();

    RetailEInvoiceAggregatedStatement_MX_DS.query().dataSourceTable(tableNum(RetailEInvoiceAggregatedStatement_MX)).addSortField(fieldNum(RetailEInvoiceAggregatedStatement_MX, PostedDateTime), SortOrder::Descending);
    RetailEInvoiceAggregatedStatement_MX_DS.query().dataSourceTable(tableNum(RetailEInvoiceAggregatedStatement_MX)).addSortField(fieldNum(RetailEInvoiceAggregatedStatement_MX, AggregatedStatementId), SortOrder::Descending);
}

public int active()
{
    int ret = super();

    element.enableButtons();

    return ret;
}

public void clicked()
{
    super();

    for (Common common = EInvoiceJour_DS.getFirst(true) ? EInvoiceJour_DS.getFirst(true) : EInvoiceJour_DS.cursor();
        common;
        common = EInvoiceJour_DS.getNext())
    {
        EInvoiceDocState_MX::construct(common).resubmitEInvoice();
    }

    RetailEInvoiceAggregatedStatement_MX_DS.research(true);
    EInvoiceJour_DS.research(true);
}

