public int active()
{
    int ret;

    ret = super();
    element.setTitleText();
    return ret;
}

public void linkActive()
{
    RetailFiscalReceipt_BR  fiscalReceipt;
    RetailTransactionTable  transaction;

    switch (element.args().dataset())
    {
        case tableNum(RetailFiscalReceipt_BR):
            fiscalReceipt = element.args().record();
            RetailFiscalReceipt_BR_ds.query().dataSourceName('RetailFiscalReceipt_BR').clearDynalinks();
            RetailFiscalReceipt_BR_ds.query().dataSourceName('RetailFiscalReceipt_BR').clearRanges();
            RetailFiscalReceipt_BR_ds.query().dataSourceName('RetailFiscalReceipt_BR').addRange(fieldNum(RetailFiscalReceipt_BR, RecId)).value(queryValue(fiscalReceipt.RecId));
            break;
        case tableNum(RetailTransactionTable):
            transaction = element.args().record();
            RetailFiscalReceipt_BR_ds.query().dataSourceName('RetailFiscalReceipt_BR').clearDynalinks();
            RetailFiscalReceipt_BR_ds.query().dataSourceName('RetailFiscalReceipt_BR').addRange(fieldNum(RetailFiscalReceipt_BR, TransactionId)).value(queryValue(transaction.transactionId));
            RetailFiscalReceipt_BR_ds.query().dataSourceName('RetailFiscalReceipt_BR').addRange(fieldNum(RetailFiscalReceipt_BR, TerminalId)).value(queryValue(transaction.terminal));
            RetailFiscalReceipt_BR_ds.query().dataSourceName('RetailFiscalReceipt_BR').addRange(fieldNum(RetailFiscalReceipt_BR, StoreId)).value(queryValue(transaction.store));
            break;
    }

    super();
}

/// <summary>
/// This method is used to prevent the creation of an incorrect link to the <c>InventProductTypeTable_BR</c>
/// caused by an incorrect relation on the table.
/// </summary>
/// <returns>
/// The line item type.
/// </returns>
display InventProductTypeId_BR itemType()
{
    return RetailFiscalReceiptLine_BR.ItemType;
}

public void init()
{
    QueryBuildDataSource    qbds;

    super();

    qbds = RetailFiscalReceiptTaxTransTotals_ds.query().dataSourceName('RetailFiscalReceiptTaxTransTotals');

    qbds.addGroupByField(fieldnum(RetailFiscalReceiptTaxTrans_BR, TaxCode));
    qbds.addSelectionField(fieldNum(RetailFiscalReceiptTaxTrans_BR, TaxBaseAmount), SelectionField::Sum);
    qbds.addSelectionField(fieldNum(RetailFiscalReceiptTaxTrans_BR, TaxAmount), SelectionField::Sum);

    qbds = qbds.addDataSource(tableNum(RetailFiscalReceiptLine_BR));
    qbds.addRange(fieldNum(RetailFiscalReceiptLine_BR, CancelledQuantity)).value(queryValue(0));
    qbds.relations(true);
}

public void tabChanged(int _FromTab, int _ToTab)
{
    super(_FromTab, _ToTab);

    // Buttons to show in Details View
    ShowGroup.visible(!TabPageGrid.isActivePage());
}

public void jumpRef()
{
    MenuFunction menuFunction;
    Args args = new Args();

    menuFunction = new MenuFunction(menuitemdisplaystr(RetailTenderTypeTable), MenuItemType::Display);

    args.lookupRecord(RetailTenderTypeTable::find(RetailTransactionPaymentTrans_BR.tenderType));

    menuFunction.run(args);
}
