[Form]
public class RetailFunctionalityProfile extends FormRun
{
    private RetailFiscalIntegrationTechnicalProfileLineDisplayHelper fiscalIntegrationTechnicalProfileLineDispalyHelper;
}

public void init()
{
    super();

    // If Prompt for Sales Rep has set to No, then disable RequireSalesRep checkbox.
    if (RetailFunctionalityProfile.PromptForSalesRep == RetailSalesRepPromptAtPOS::NoPrompt)
    {
        SalesRep_RequireSalesRep.enabled(false);
    }

    // If DenominationsToDisplay is set to a value different than GreaterOrEqualToAmountDue, then disable UseQuickPayShortcuts checkbox.
    if (RetailFunctionalityProfile.DenominationsToDisplay != RetailDenominationsToDisplay::GreaterOrEqualToAmountDue)
    {
        CashDenominations_UseQuickPayShortcuts.enabled(false);
    }

    // If the advanced auto charges feature is not in use, then disable ManualChargeCalculation toggle
    if (!RetailParameters::isRetailAutoChargesEnabledAndInUse())
    {
        RetailFunctionalityProfile_ManualChargeCalculation.visible(false);
    }

    // If the fiscal integration feature is not in use, then disable FiscalRegistrationProcess tab page.
    if (!RetailSharedParameters::find().EnableFiscalIntegration)
    {
        FiscalRegistrationProcess.visible(false);
    }

    // If the fiscal integration feature or the fiscal integration internal and external services feature are not in use,
    // then disable FiscalServices tab page.
    if (!RetailSharedParameters::find().EnableFiscalIntegration)
    {
        FiscalServices.visible(false);
    }

    // Set daily shift closing fields visible based on the current country region.
    this.setShiftClosingFieldsVisible();

    fiscalIntegrationTechnicalProfileLineDispalyHelper = new RetailFiscalIntegrationTechnicalProfileLineDisplayHelper(
        FiscalConnectorGroup,
        FiscalDeviceGroup,
        FiscalSettingsGroup,
        RetailFunctionalityProfileFiscalIntegrationTechnicalProfileLine);

    CustomerOrder_CreateCustomerOrderWaitTimeout.visible(FeatureStateProvider::isFeatureEnabled(RetailEnhancedAsyncOrderCreationFeature::instance()));
    CustomerOrder_PromptEditContactForAsyncOrderCancellation.visible(FeatureStateProvider::isFeatureEnabled(RetailAsyncOrderCancellationFeature::instance()));
    CustomerOrder_SkipSalesInvoiceSelectionDuringReturn.visible(FeatureStateProvider::isFeatureEnabled(RetailUnifiedReturnUXImprovementFeature::instance()));

    CustomerSearch_DefaultCustomerSearchMode.visible(RetailCustomerSearchFeatureControl::customerSearchDefaultValueEnabled());

    this.setIsFiscalCustomerRequiredForTransactionVisible();
    this.setDisableEditingOfPartiallyFulfilledOrdersVisible();

    this.setEditabilityForCreateAsyncCustomerField();
    this.setUseFinancialReconcialiationInStoreVisible();
    this.setInventAvailabilityCalculationVisible();

    Copilot.visible(RetailStoreCommerceCopilotFeature::isEnabled() && RetailSharedParameters::find().EnableStoreCommerceCopilot);
}

/// <summary>
/// "CreateCustomerOrdersWithAsyncFallback" depends on "CreateOfflineCustomerOrders": we will gray out the former toggle if the latter config is disabled.
/// </summary>
private void updateCustomerOrderCreationModeControls()
{
    CustomerOrder_CreateCustomerOrdersWithAsyncFallback.enabled(RetailFunctionalityProfile.CreateOfflineCustomerOrders == NoYes::Yes);
}

private void updateStaffControls()
{
    if (RetailFunctionalityProfile.EmployeeLogonType == RetailEmployeeLogonType::AzureActiveDirectory
        || RetailFunctionalityProfile.EmployeeLogonType == RetailEmployeeLogonType::AzureActiveDirectorySingleSignOn)
    {
        Staff1_showStaffListAtLogOn.enabled(false);
        Staff1_staffBarcodeLogon.enabled(false);
        Staff1_StaffBarcodeLogonRequiresPassword.enabled(false);
        Staff1_staffCardLogon.enabled(false);
        Staff1_StaffCardLogonRequiresPassword.enabled(false);
        Staff1_limitStaffListToStore.enabled(false);
    }
    else if(RetailFunctionalityProfile.EmployeeLogonType == RetailEmployeeLogonType::RetailIdentityProvider)
    {
        Staff1_showStaffListAtLogOn.enabled(true);
        Staff1_staffBarcodeLogon.enabled(true);
        Staff1_StaffBarcodeLogonRequiresPassword.enabled(true);
        Staff1_staffCardLogon.enabled(true);
        Staff1_StaffCardLogonRequiresPassword.enabled(true);
        Staff1_limitStaffListToStore.enabled(true);
    }
}

/// <summary>
/// Sets the allowEdit property on the Async Customer creation field.
/// </summary>
private void setEditabilityForCreateAsyncCustomerField()
{
    // If the Enable async address creation feature is enabled, disable editing the CreateAsyncCustomers switch.
    boolean isEnableAsyncAddressCreationFeatureEnabled = RetailAsyncAddressCreationFeature::isEnabled();
    Customers_CreateAsyncCustomers.allowEdit(!isEnableAsyncAddressCreationFeatureEnabled);
}

/// <summary>
/// Sets daily shift closing fields visible based on the current country region.
/// </summary>
private void setShiftClosingFieldsVisible()
{
    #ISOCountryRegionCodes

    boolean visible = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoFR]);
    DailyShiftClosing.visible(visible);
}

/// <summary>
/// Sets IsFiscalCustomerRequiredForTransaction visible based on the current country region and feature enabling.
/// </summary>
private void setIsFiscalCustomerRequiredForTransactionVisible()
{
    #ISOCountryRegionCodes

    boolean visible = (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIT]))
        || (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]))
        || (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]));
    TaxParameters_IsFiscalCustomerRequiredForTransaction.visible(visible);
}

/// <summary>
/// Sets DisableEditingOfPartiallyFulfilledOrders visible based on the feature state.
/// </summary>
private void setDisableEditingOfPartiallyFulfilledOrdersVisible()
{
    boolean visible = RetailAllowPartialOrderEditingFeatureExposure::isEnabledInFeatureManagement();

    CustomerOrder_DisableEditingOfPartiallyFulfilledOrders.visible(visible);
}

private void enableClearReceiptResetDate()
{
    RetailReceiptResetConfiguration configuration = RetailReceiptResetConfiguration::findByProfileId(retailFunctionalityProfile.profileId);

    FormButtonControl clearReceiptResetDateButton = element.ClearReceiptResetDate;

    clearReceiptResetDateButton.enabled(configuration.Enabled);
}

/// <summary>
/// Sets UseFinancialReconcialiationInStore visible based on the feature state.
/// </summary>
private void setUseFinancialReconcialiationInStoreVisible()
{
    boolean visible = RetailEodTrickleFeedFeatureExposure::isEnabled();

    CashManagement_UseFinancialReconcialiationInStore.visible(visible);
}

/// <summary>
/// Is called when a particular profile is selected.
/// </summary>
/// <returns></returns>
public int active()
{
    int ret;

    ret = super();
    element.updateCustomerOrderCreationModeControls();
    element.updateStaffControls();
    RetailFunctionalityProfile_ds.setShiftClosingFieldsEnabled();

    var isRemoteCustomerSearchEnabled = RetailFunctionalityProfile.SearchCustomersInAx == NoYes::Yes;
    CustomerSearch_DefaultCustomerSearchMode.enabled(isRemoteCustomerSearchEnabled);

    boolean isDenominationsToDisplayGreaterOrEqualToAmountDue = RetailFunctionalityProfile.DenominationsToDisplay == RetailDenominationsToDisplay::GreaterOrEqualToAmountDue;
    CashDenominations_UseQuickPayShortcuts.enabled(isDenominationsToDisplayGreaterOrEqualToAmountDue);

    element.findOrCreateRegistrationProcessIdRange();

    element.enableClearReceiptResetDate();

    // Show warning if <c>daysTransactionsExists</c> field value is less than or equal to zero.
    if (RetailFunctionalityProfile.RecId != 0 && RetailFunctionalityProfile.daysTransactionsExists <= 0)
    {
        warning ("@Retail:InvalidDaysTransactionsExistsWarning");
    }

    return ret;
}

/// <summary>
/// Enable shift closing Interval and Time fields when the Check field is set to Yes.
/// </summary>
public void setShiftClosingFieldsEnabled()
{
    boolean enabled = RetailFunctionalityProfile.ShiftClosingCheck;
    RetailFunctionalityProfile_ds.object(fieldNum(RetailFunctionalityProfile, ShiftClosingInterval)).enabled(enabled);
    RetailFunctionalityProfile_ds.object(fieldNum(RetailFunctionalityProfile, ShiftClosingTime)).enabled(enabled);
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    boolean isOptimizedProductAvailabilityJobEnabled = FeatureStateProvider::isFeatureEnabled(RetailProductAvailabilityOptimizationFeature::instance());
    if (RetailFunctionalityProfile.InventAvailabilityCalculationMode == RetailInventAvailabilityCalculationMode::Channel
        && !isOptimizedProductAvailabilityJobEnabled)
    {
        Message::Add(MessageSeverity::Error, "@RetailInventory:OptimizedProductAvailabilityNotEnabledForChannelSideCalculation");
        ret = false;

    }

    return ret;
}

/// <summary>
/// Enable fields related to the restricting shift duration functionality,
/// when this field is set to Yes.
/// </summary>
public void modified()
{
    super();

    RetailFunctionalityProfile_ds.setShiftClosingFieldsEnabled();
}

public int active()
{
    int active = super();

    fiscalIntegrationTechnicalProfileLineDispalyHelper.displayFiscalTechnicalProperties();

    return active;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    Query query = RetailFiscalIntegrationTechnicalProfileLineDisplayHelper::getTechnicalProfilesAvailableAtRegister();
    SysTableLookup lookup = SysTableLookup::newParameters(tableNum(RetailFiscalIntegrationTechnicalProfile), _formControl, true);
    lookup.parmQuery(query);

    // Add the fields to be shown in the lookup form.
    lookup.addLookupfield(fieldNum(RetailFiscalIntegrationTechnicalProfile, ProfileId));
    lookup.addLookupfield(fieldNum(RetailFiscalIntegrationTechnicalProfile, Description));

    lookup.performFormLookup();
}

/// <summary>
/// If unchecked, disable CreateCustomerOrdersWithAsyncFallback field.
/// </summary>
/// <returns>True if the field is modified successfully; false otherwise.</returns>
public boolean modified()
{
    boolean ret;

    if (!this.checked())
    {
        // If CreateOfflineCustomerOrders is disabled, disable CreateCustomerOrdersWithAsyncFallback field as well.
        RetailFunctionalityProfile.CreateCustomerOrdersWithAsyncFallback = NoYes::No;
    }

    ret = super();

    element.updateCustomerOrderCreationModeControls();

    return ret;
}

/// <summary>
/// The staff logon controls would be set to NoYes::No and disabled if EmployeeLogonTypeGroup_EmployeeLogonType
/// has value RetailEmployeeLogonType::AzureActiveDirectory. Otherwise enable them.
/// </summary>
/// <returns>true for success, false for failure.</returns>
public boolean modified()
{
    boolean ret;

    ret = super();
    element.updateStaffControls();
    if (this.selection() == RetailEmployeeLogonType::AzureActiveDirectory ||
        this.selection() == RetailEmployeeLogonType::AzureActiveDirectorySingleSignOn)
    {
        RetailFunctionalityProfile.showStaffListAtLogOn = NoYes::No;
        RetailFunctionalityProfile.staffBarcodeLogon = NoYes::No;
        RetailFunctionalityProfile.StaffBarcodeLogonRequiresPassword = NoYes::No;
        RetailFunctionalityProfile.staffCardLogon = NoYes::No;
        RetailFunctionalityProfile.StaffCardLogonRequiresPassword = NoYes::No;
        RetailFunctionalityProfile.limitStaffListToStore = NoYes::No;
    }

    return ret;
}

/// <summary>
/// The CashDenominations_UseQuickPayShortcuts would be set to NoYes::No and disabled if CashDenominations_DenominationsToDisplay
/// has value RetailDenominationsToDisplay::GreaterOrEqualToAmountDue. Otherwise enable it.
/// </summary>
/// <returns>True if the field is modified successfully; false otherwise.</returns>
public boolean modified()
{
    boolean ret;
    ret = super();

    boolean isDenominationsToDisplayGreaterOrEqualToAmountDue = this.selection() == RetailDenominationsToDisplay::GreaterOrEqualToAmountDue;
    if (!isDenominationsToDisplayGreaterOrEqualToAmountDue)
    {
        RetailFunctionalityProfile.UseQuickPayShortcuts = NoYes::No;
    }
    CashDenominations_UseQuickPayShortcuts.enabled(isDenominationsToDisplayGreaterOrEqualToAmountDue);

    return ret;
}

/// <summary>
/// When checked, validate if "CustomerOrder_CreateOfflineCustomerOrders" should be checked.
/// </summary>
/// <returns>Show warning message, if "CustomerOrder_CreateOfflineCustomerOrders" is not checked.</returns>
public boolean modified()
{
    boolean ret;

    if (this.checked() && !CustomerOrder_CreateOfflineCustomerOrders.checked())
    {
        warning ("@Retail:EnableCreateOfflineCustomerOrdersWarning");
    }

    ret = super();

    return ret;
}

/// <summary>
/// Set whether remote customer search mode is enabled.
/// </summary>
/// <returns>True if the field is modified successfully; false otherwise.</returns>
public boolean modified()
{
    boolean ret;
    ret = super();

    var isRemoteCustomerSearchEnabled = this.checked();
    CustomerSearch_DefaultCustomerSearchMode.enabled(isRemoteCustomerSearchEnabled);

    return ret;
}

/// <summary>
/// Set the default customer search type based on the specified customer search mode.
/// </summary>
/// <returns>True if the field is modified successfully; false otherwise.</returns>
public boolean modified()
{
    boolean ret;
    ret = super();

    if(this.selection() != RetailCustomerSearchMode::All)
    {
        retailFunctionalityProfile.CustomerSearchDefault = this.selection();
        retailFunctionalityProfile_ds.refresh();
    }

    this.showHideDefault();

    return ret;
}

private void showHideDefault()
{
    boolean enabled = (retailFunctionalityProfile.CustomerSearchMode == RetailCustomerSearchMode::All);
    RetailFunctionalityProfile_CustomerSearchDefault.enabled(enabled);
}

/// <summary>
/// SalesRep_RequireSalesRep would be disable if SalesRep_PromptForSalesRep have
/// RetailSalesRepPromptAtPOS::NoPrompt set and update RetailFunctionalityProfile.RequireSalesRep set to NoYes::No,
/// Otherwise enable it.
/// </summary>
/// <returns>true for success, false for failure.</returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    if ( this.selection() == RetailSalesRepPromptAtPOS::NoPrompt)
    {
        RetailFunctionalityProfile.RequireSalesRep = NoYes::No;
        SalesRep_RequireSalesRep.enabled(false);
        RetailFunctionalityProfile_DS.executeQuery();
        RetailFunctionalityProfile_DS.refresh();
    }
    else
    {
        SalesRep_RequireSalesRep.enabled(true);
    }

    return ret;
}

/// <summary>
/// Runs when clicked event is triggered.
/// </summary>
public void clicked()
{
    super();

    if (Box::confirm("@RetailReceiptReset:ClearResetDateConfirm"))
    {
        RetailReceiptResetConfiguration::clearRetailReceiptResetConfiguration(retailFunctionalityProfile.profileId);

        RetailFunctionalityProfile_ds.research(true);
    }
}
