public void selectionChanged()
{
    // Check if we need to update the lock and balance values.
    if (RetailGiftCardTable.reserved)
    {
        RetailGiftCardTable.tryReleaseCardLock();
    }

    RetailGiftCardTable_ds.reread();
    RetailGiftCardTable_ds.refresh();

    if (RetailGiftCardTable::isGiftCardDataShared())
    {
        RetailGiftCardTransactions_ds.query(RetailGiftCardTable::getGiftCardTransactionsQuery(RetailGiftCardTable.entryId));
        RetailGiftCardTransactions_ds.executeQuery();
    }
    else
    {
        RetailGiftCardTransactions_ds.research(true);
    }

    super();
}

void clicked()
{
    RetailGiftCardTable.unlockCard(false, RetailGiftCardOperation::ManualUnlock);

    RetailGiftCardTable_ds.reread();
    RetailGiftCardTable_ds.refresh();

    RetailGiftCardTransactions_ds.research(true);
    super();
}
