/// <summary>
/// Adds a connector property to the payment connector property bag.
/// </summary>
/// <param name="paymentConnectorPropertyXML">The payment connector (merchant account propertry) XML string.</param>
/// <param name="propertyName">The name of the connector property.</param>
/// <param name="propertyValue">The value of the connector property.</param>
/// <returns>
/// The new payment connector property XMl string which contains the added connector property.
/// </returns>
private str addConnectorProperty(str paymentConnectorPropertyXML, str propertyName, str propertyValue)
{
    // Convert payment connector XML string into Payment Property bag object.
    // <c>CreditCardPaymentProperties</c> is a wrapper class in the payment SDK that contains an array of payment properties. It is used as a payment property bag.
    CreditCardPaymentProperties paymentPropertyBag = CreditCardPaymentProperties::fromXmlString(paymentConnectorPropertyXML);

    // Removing any existing property with the same namespace and name to prevent duplication
    paymentPropertyBag.remove(PaymentSDK.Constants.GenericNamespace::get_Connector(), propertyName);

    // Create a new connector property object to store the name of the connector.
    // and add the connector property to the property bag if it not exist there
    CreditCardPaymentProperty paymentProperty = CreditCardPaymentProperty::newValue(PaymentSDK.Constants.GenericNamespace::get_Connector(), propertyName, propertyValue);
    paymentProperty.parmIsReadonly(FalseTrue::True);
    paymentPropertyBag.add(paymentProperty);

    return CreditCardPaymentProperties::convertToXml(paymentPropertyBag.toArray(), false);
}

private void clearFiscalTechnicalDynamicControls()
{
    while (FiscalDeviceGroup.controlCount() > 0)
    {
        FiscalDeviceGroup.formRun().design().removeControl(FiscalDeviceGroup.controlNum(1).id());
    }

    while (FiscalConnectorGroup.controlCount() > 0)
    {
        FiscalConnectorGroup.formRun().design().removeControl(FiscalConnectorGroup.controlNum(1).id());
    }

    while (FiscalSettingsGroup.controlCount() > 0)
    {
        FiscalSettingsGroup.formRun().design().removeControl(FiscalSettingsGroup.controlNum(1).id());
    }
}

private void displaySelectedConnectorProperties()
{
    boolean shouldDisplayPropertyGroup = RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorName != null &&
                    RetailHardwareProfilePaymentConnectorTmp.HardwareProfileId == RetailHardwareProfile.profileId;

    if (shouldDisplayPropertyGroup)
    {
        connectorSetup.displayPropertyValuesFromXmlString(RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorName, RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorProperties);
    }

    element.SetDynamicsOnlineConfiguration();

    // Dynamics groups
    AccountGroup.visible(shouldDisplayPropertyGroup);
    EmptyConnectorPropertyText.visible(!shouldDisplayPropertyGroup);
}

