}

/// <summary>
/// Gets an xml string containig the combined merchant properties of all the connectors configured for the specified hardware profile.
/// </summary>
/// <param name = "hardwareProfileId">The hardware profile Id.</param>
/// <returns>Returns a combined payment connector (merchant account) property for the specified hardware profile.</returns>
private str getCombinedConnectorPropertiesAsXml(RetailHardwareProfileId hardwareProfileId, RetailHardwareProfilePaymentConnectorTmp paymentConnectorTmpDatasource)
{
    CreditCardPaymentProperties eftConnectorProperties = CreditCardPaymentProperties::construct();
    eftConnectorProperties = element.getCombinedConnectorPropertiesAsXmlFromTable(hardwareProfileId, eftConnectorProperties, paymentConnectorTmpDatasource);

    return eftConnectorProperties.toXmlString();
}

/// <summary>
/// Gets CreditCardPaymentProperties object containing the combined merchant properties of all the connectors configured for the specified hardware profile.
/// </summary>
/// <param name = "hardwareProfileId">The hardware profile Id.</param>
/// <param name = "eftConnectorProperties">CreditCard payment properties for adding new records</param>
/// <param name = "paymentConnectorTmpDatasource">Payment connectors data source to retrieve records for iteration</param>
/// <param name = "currentRecId">RecId to skip from iteration, by default -1 </param>
/// <returns>Returns a combined payment connector (merchant account) property for the specified hardware profile.</returns>
private CreditCardPaymentProperties getCombinedConnectorPropertiesAsXmlFromTable(RetailHardwareProfileId hardwareProfileId, CreditCardPaymentProperties eftConnectorProperties, RetailHardwareProfilePaymentConnectorTmp paymentConnectorTmpDatasource, int currentRecId = -1)
{
    RetailHardwareProfilePaymentConnectorTmp hardwareProfilePaymentConnectorTmp;
    hardwareProfilePaymentConnectorTmp.linkPhysicalTableInstance(paymentConnectorTmpDatasource);

     // Iterate through each of the connectors of the specified hardware profile and
     // combine the payment properties into one CreditCardPaymentProperties object.
    while select * from hardwareProfilePaymentConnectorTmp
            order by hardwareProfilePaymentConnectorTmp.PaymentConnectorSequence
            where hardwareProfilePaymentConnectorTmp.HardwareProfileId == hardwareProfileId
                    && hardwareProfilePaymentConnectorTmp.RecId != currentRecId
     {
         str connectorName = hardwareProfilePaymentConnectorTmp.PaymentConnectorName;
         str connectorProperties = hardwareProfilePaymentConnectorTmp.PaymentConnectorProperties;
        //add the connectorId and connectorName to the merchant payment property xml.
         connectorProperties = element.addConnectorProperty(connectorProperties, PaymentSDK.Constants.ConnectorProperties::get_ConnectorName(), connectorName);
         eftConnectorProperties.add(element.constructConnectorProperty(hardwareProfilePaymentConnectorTmp.PaymentConnectorSequence, connectorProperties));
     }
    return eftConnectorProperties;
}

