/// <summary>
/// Gets an xml string with combined merchant properties currently edited by user on form. Includes of all connectors configured for the specified hardware profile.
/// </summary>
/// <param name = "hardwareProfileId">The hardware profile Id.</param>
/// <returns>XML with combined connectors properties for specific hardware profile with user editions</returns>
private str getEditedMerchantPropertiesAsXml(RetailHardwareProfileId hardwareProfileId, boolean isLegacyConnector = false)
{
    CreditCardPaymentProperties eftConnectorProperties = CreditCardPaymentProperties::construct();

    // Adding current connector into CreditCardPaymentProperties object.
    str connectorName = RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorName;
    str connectorProperties = isLegacyConnector ? legacyConnectorSetup.getPropertyValuesAsXmlString(connectorName) : connectorSetup.getPropertyValuesAsXmlString(connectorName);

    //add the connectorId and connectorName to the merchant payment property xml.
    connectorProperties = element.addConnectorProperty(connectorProperties, PaymentSDK.Constants.ConnectorProperties::get_ConnectorName(), connectorName);
    eftConnectorProperties.add(element.constructConnectorProperty(RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorSequence, connectorProperties));
    eftConnectorProperties = element.getCombinedConnectorPropertiesAsXmlFromTable(hardwareProfileId, eftConnectorProperties, RetailHardwareProfilePaymentConnectorTmp, RetailHardwareProfilePaymentConnectorTmp.RecId);

    //Generating XMl with combined properties for further validation
    return eftConnectorProperties.toXmlString();
}

/// <summary>
/// Gets an xml string containig the merchant properties and does not wrap it in multi-connector formatting.
/// </summary>
/// <param name = "hardwareProfileId">The hardware profile Id.</param>
/// <returns>Returns a single payment connector (merchant account) property for the specified hardware profile.</returns>
private str getConnectorPropertiesInSingleConnectorFormat(RetailHardwareProfileId hardwareProfileId, RetailHardwareProfilePaymentConnectorTmp paymentConnectorTmpDatasource)
{
    RetailHardwareProfilePaymentConnectorTmp hardwareProfilePaymentConnectorTmp;
    hardwareProfilePaymentConnectorTmp.linkPhysicalTableInstance(paymentConnectorTmpDatasource);

    // Get the first connector and its payment properties.
    select firstonly hardwareProfilePaymentConnectorTmp
        order by hardwareProfilePaymentConnectorTmp.PaymentConnectorSequence
        where hardwareProfilePaymentConnectorTmp.HardwareProfileId == hardwareProfileId;

    return hardwareProfilePaymentConnectorTmp.RecId
        ? hardwareProfilePaymentConnectorTmp.PaymentConnectorProperties
        : '';
}

/// <summary>
/// Create a Property for the Connector, using its payment properties as its value of type PropertyList.
/// </summary>
/// <param name="_sequence"> The sequence Number of the property.</param>
/// <param name="_xml"> A XML string of the connector properties.</param>
/// <returns>
/// A <c>CreditCardPaymentProperty</c> for the Connector.
/// </returns>
private CreditCardPaymentProperty constructConnectorProperty(int _sequence, str _xml)
{
    // Create a new property for the Connector and adds its properties as a propertyList
    CreditCardPaymentProperty multipleConnectorProperty = CreditCardPaymentProperty::newValue(
        PaymentSDK.Constants.GenericNamespace::get_Connector(),
        PaymentSDK.Constants.ConnectorProperties::get_Properties(),
    CreditCardPaymentProperties::fromXmlString(_xml));

    multipleConnectorProperty.parmSequenceNumber(_sequence);

    return multipleConnectorProperty;
}

void ExportDOPSSettings()
{
    var merchant = RetailHardwareProfile.secureMerchantPropertiesEdit(false, '');
    CreditCardPaymentProperties properties = CreditCardPaymentProperties::fromXmlString(merchant);
    // Find properties and update with passed in parameters
    CreditCardPaymentProperty property = properties.find(PaymentSDK.Constants.GenericNamespace::get_MerchantAccount(),
        PaymentSDK.Constants.MerchantAccountProperties::get_MerchantId());
    if (property)
    {
        RetailHardwareProfile.EFTMerchantId = property.parmStringValue();
    }

    property = properties.find(PaymentSDK.Constants.GenericNamespace::get_MerchantAccount(), PaymentSDK.Constants.MerchantAccountProperties::get_ServiceAccountId());
    if (property)
    {
        RetailHardwareProfile.EFTCompanyId = property.parmStringValue();
    }
}

