public void init()
{
    // <GEERU>
    #ISOCountryRegionCodes
    // </GEERU>

    super();

    RetailPaymentConnectorLookupTempTable::initializePaymentConnectors(retailPaymentConnectorLookupTempTable, 'RetailHardwareProfile');
    connectorSetup = CreditCardConnectorSetup::construct();
    str onPropertyFieldModifiedEventHandlerMethodName = 'onPropertyFieldModifiedCallBack'; // This is the name of the method that is called back if the value in
    // any of the controls in the AccountGroup section are modified.

    // NB: AccountGroup is a variable of FormControlGroup type referecing the AccountGroup control.
    connectorSetup.setParentControl(AccountGroup, literalStr(onPropertyFieldModifiedCallBack));

    PolicyHtmlViewer.parmHtml(strFmt(
        "<html><body>%1&nbsp;<a href='https://go.microsoft.com/fwlink/?linkid=2038552&clcid=0x409'>%2</a></body></html>",
        "@Retail:PaymentConnectorPolicyText",
        "@Retail:PaymentConnectorPolicyLinkText"));

    if(RetailUtilities::isRetailCDXBackwardCompatibilityEnabled())
    {
        RetailPaymentConnectorLookupTempTable::initializeLegacyPaymentConnectors(retailLegacyPaymentConnectorLookupTempTable);
        legacyConnectorSetup = RetailCreditCardLegacyConnectorSetup::construct() as RetailCreditCardLegacyConnectorSetup;
        str onLegacyPropertyFieldModifiedEventHandlerMethodName = 'onLegacyPropertyFieldModifiedCallBack'; // This is the name of the method that is called back if the value in
        // any of the controls in the LegacyAccountGroup section are modified.
        legacyConnectorSetup.setParentControl(LegacyAccountGroup, onLegacyPropertyFieldModifiedEventHandlerMethodName);
    }

    retailFiscalIntegrationTechnicalProfileLineDisplayHelper = new RetailFiscalIntegrationTechnicalProfileLineDisplayHelper(
        FiscalConnectorGroup,
        FiscalDeviceGroup,
        FiscalSettingsGroup,
        RetailHardwareProfileFiscalIntegrationTechnicalProfileLine);

    // If the fiscal integration feature is not in use, then disable FiscalPeripherals tab page.
    if (!RetailSharedParameters::find().EnableFiscalIntegration)
    {
        FiscalPeripherals.visible(false);
    }

    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        FiscalPrinter.visible(true);
    }
    // </GEERU>

    if (RetailPaymentsShowPinPadAllowNfcPaymentsFlight::instance().isEnabled())
    {
        PinPadGroup_pinPadAllowNfcPayments.visible(true);
    }

    RetailSelfServiceMigrationHelper::populateDefaultPackageReferencesFromRetailSharedParameters();
    selfServicePackageManager = new RetailSelfServiceMergedPackageManager();
}

