    RetailHardwareProfile_ds.object(fieldnum(RetailHardwareProfile,EFTServerPort)).visible(!isPaymentSDK);
    RetailHardwareProfile_ds.object(fieldnum(RetailHardwareProfile,captureExtraData)).visible(!isPaymentSDK);

    RetailHardwareProfile_ds.object(fieldnum(RetailHardwareProfile,EFTServerName)).visible(!isPaymentSDK);
    RetailHardwareProfile_ds.object(fieldnum(RetailHardwareProfile,EFTDescription)).visible(!isPaymentSDK);
    RetailHardwareProfile_ds.object(fieldnum(RetailHardwareProfile,EFTServerPort)).visible(!isPaymentSDK);
    RetailHardwareProfile_ds.object(fieldnum(RetailHardwareProfile,captureExtraData)).visible(!isPaymentSDK);

