public int task(int _taskId)
{
    #Task
    int ok;

    if (_taskId == #taskEsc) // Leaving the form without saving changes
    {
        RetailHardwareProfile_ds.forceWrite(false);
    }
    ok = super(_taskId);

    if (_taskId == 2839) // Control-F5, Restore
    {
        RetailHardwareProfile_ds.active();
    }

    return ok;
}

