private void loadConnectorDataToUI()
{
    // decrypt the connector properties XML string in RetailHardwareProfile
    str decryptedConnectorPropertyString = RetailHardwareProfile.secureMerchantPropertiesEdit(false, '');

    if ((RetailHardwareProfile.EFT == RetailExternalNone::PaymentSDK) && (decryptedConnectorPropertyString != ''))
    {
        select RetailHardwareProfilePaymentConnectorTmp
                where RetailHardwareProfilePaymentConnectorTmp.HardwareProfileId == RetailHardwareProfile.profileId;

        // Check if the record already exists in the temp table; Else we need to insert it.
        if (RetailHardwareProfilePaymentConnectorTmp.RecId == 0)
        {
            PaymentSDK.PaymentProperty[] allPaymentConnectorProperties = PaymentSDK.PaymentProperty::ConvertXMLToPropertyArray(decryptedConnectorPropertyString);
            PaymentSDK.PaymentProperty firstPaymentProperty = allPaymentConnectorProperties.GetValue(0);

            boolean isConnectorPropertiesXmlInUpdatedFormat = System.String::Equals(firstPaymentProperty.get_Namespace(), PaymentSDK.Constants.GenericNamespace::get_Connector(), System.StringComparison::OrdinalIgnoreCase)
                        && System.String::Equals(firstPaymentProperty.get_Name(), PaymentSDK.Constants.ConnectorProperties::get_Properties(), System.StringComparison::OrdinalIgnoreCase);

            connectorSequence = 1; // initialize conector sequence.
            // Check if the xml holds properties for multiple connectors.
            if (isConnectorPropertiesXmlInUpdatedFormat)
            {
                for (int arrayIndex = 0; arrayIndex < allPaymentConnectorProperties.get_Length(); arrayIndex++)
                {
                    PaymentSDK.PaymentProperty paymentProperty = allPaymentConnectorProperties.GetValue(arrayIndex);
                    CreditCardPaymentProperties singleConnectorProperties = CreditCardPaymentProperties::fromProperties(paymentProperty.get_PropertyList());
                    CreditCardPaymentProperty connectorName = singleConnectorProperties.find(PaymentSDK.Constants.GenericNamespace::get_Connector(), PaymentSDK.Constants.ConnectorProperties::get_ConnectorName());

                    RetailHardwareProfilePaymentConnectorTmp.HardwareProfileId = RetailHardwareProfile.profileId;
                    RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorSequence = connectorSequence;
                    RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorName = connectorName.parmStringValue();
                    RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorProperties = singleConnectorProperties.toXmlString();
                    RetailHardwareProfilePaymentConnectorTmp.insert();

                    connectorSequence++;
                }
            }
            else
            {
                // There are no multi connectors defined in the xml. This is the case for the older formats of the hardware profile.
                // In this single connector per hardware profile format - the name of the connector is saved in the EFTConnectorName field
                // and the corresponding payment properties of that single connector is saved in the secureMerchantPropeties field.
                CreditCardPaymentProperties singleConnectorProperties = CreditCardPaymentProperties::fromProperties(allPaymentConnectorProperties);

                RetailHardwareProfilePaymentConnectorTmp.HardwareProfileId = RetailHardwareProfile.profileId;
                RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorSequence = connectorSequence;
                RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorName = RetailHardwareProfile.EFTConnectorName;
                RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorProperties = singleConnectorProperties.toXmlString();
                RetailHardwareProfilePaymentConnectorTmp.insert();
                connectorSequence++;
            }

            isInitialLoading = true;
            RetailHardwareProfilePaymentConnectorTmp_ds.research(true);
            element.displaySelectedConnectorProperties();
            RetailHardwareProfilePaymentConnectorTmp_ds.write();
        }
        else
        {
            // Record already exists in the temp table, update the UI.
            element.displaySelectedConnectorProperties();
            isInitialLoading = false;
        }
    }

    // Shared drawer enforcement.
    Drawer.enabled(RetailHardwareProfile.drawer2UseCashDrawerPool ? false : true);
    Drawer2.enabled(RetailHardwareProfile.drawerUseCashDrawerPool ? false : true);

    retailFiscalIntegrationTechnicalProfileLineDisplayHelper.displayFiscalTechnicalProperties();

    element.hideControlsForRetailTrial();
}

private void loadLegacyConnectorDataToUI()
{
    if ((RetailHardwareProfile.EFT == RetailExternalNone::PaymentSDK) && (RetailHardwareProfile.EFTConnectorProperties != ''))
    {
        select RetailHardwareProfileLegacyPaymentConnectorTmp
                where RetailHardwareProfileLegacyPaymentConnectorTmp.HardwareProfileId == RetailHardwareProfile.profileId;

        // Check if the record already exists in the temp table; Else we need to insert it.
        if (RetailHardwareProfileLegacyPaymentConnectorTmp.RecId == 0)
        {
            PaymentSDK.PaymentProperty[] allPaymentConnectorProperties = PaymentSDK.PaymentProperty::ConvertXMLToPropertyArray(RetailHardwareProfile.EFTConnectorProperties);
            PaymentSDK.PaymentProperty firstPaymentProperty = allPaymentConnectorProperties.GetValue(0);

            boolean isConnectorPropertiesXmlInUpdatedFormat = System.String::Equals(firstPaymentProperty.get_Namespace(), PaymentSDK.Constants.GenericNamespace::get_Connector(), System.StringComparison::OrdinalIgnoreCase)
                        && System.String::Equals(firstPaymentProperty.get_Name(), PaymentSDK.Constants.ConnectorProperties::get_Properties(), System.StringComparison::OrdinalIgnoreCase);

            legacyConnectorSequence = 1; // initialize conector sequence.
            // Check if the xml holds properties for multiple connectors.
            if (isConnectorPropertiesXmlInUpdatedFormat)
            {
                for (int arrayIndex = 0; arrayIndex < allPaymentConnectorProperties.get_Length(); arrayIndex++)
                {
                    PaymentSDK.PaymentProperty paymentProperty = allPaymentConnectorProperties.GetValue(arrayIndex);
                    CreditCardPaymentProperties singleConnectorProperties = CreditCardPaymentProperties::fromProperties(paymentProperty.get_PropertyList());
                    CreditCardPaymentProperty connectorName = singleConnectorProperties.find(PaymentSDK.Constants.GenericNamespace::get_Connector(), PaymentSDK.Constants.ConnectorProperties::get_ConnectorName());

                    RetailHardwareProfileLegacyPaymentConnectorTmp.HardwareProfileId = RetailHardwareProfile.profileId;
                    RetailHardwareProfileLegacyPaymentConnectorTmp.PaymentConnectorSequence = legacyConnectorSequence;
                    RetailHardwareProfileLegacyPaymentConnectorTmp.PaymentConnectorName = connectorName.parmStringValue();
                    RetailHardwareProfileLegacyPaymentConnectorTmp.PaymentConnectorProperties = singleConnectorProperties.toXmlString();
                    RetailHardwareProfileLegacyPaymentConnectorTmp.insert();

                    legacyConnectorSequence++;
                }
            }
            else
            {
                // There are no multi connectors defined in the xml. This is the case for the older formats of the hardware profile.
                // In this single connector per hardware profile format - the name of the connector is saved in the EFTConnectorName field
                // and the corresponding payment properties of that single connector is saved in the secureMerchantPropeties field.
                CreditCardPaymentProperties singleConnectorProperties = CreditCardPaymentProperties::fromProperties(allPaymentConnectorProperties);

                RetailHardwareProfileLegacyPaymentConnectorTmp.HardwareProfileId = RetailHardwareProfile.profileId;
                RetailHardwareProfileLegacyPaymentConnectorTmp.PaymentConnectorSequence = legacyConnectorSequence;
                RetailHardwareProfileLegacyPaymentConnectorTmp.PaymentConnectorName = RetailHardwareProfile.EFTConnectorName;
                RetailHardwareProfileLegacyPaymentConnectorTmp.PaymentConnectorProperties = singleConnectorProperties.toXmlString();
                RetailHardwareProfileLegacyPaymentConnectorTmp.insert();
                legacyConnectorSequence++;
            }

            RetailHardwareProfileLegacyPaymentConnectorTmp_ds.research(true);
            element.displaySelectedLegacyConnectorProperties();
            RetailHardwareProfileLegacyPaymentConnectorTmp_ds.write();
        }
        else
        {
            // Record already exists in the temp table, update the UI.
            element.displaySelectedLegacyConnectorProperties();
        }
    }
}

/// <summary>
/// Validate current combined merchant properties XML. Criterias:
/// 1. Do not reached max length for secure string = 65536
/// </summary>
/// <param name = "isLegacyConnectors">flag if this is check for legacyConnectos</param>
/// <returns>True if passed criterias of check, otherwise false</returns>
private boolean merchantPropertiesCombinedXMLValidation(boolean isLegacyConnectors = false)
{
    str xmlProperties;

    if(isLegacyConnectors)
    {
        str connectorName = RetailHardwareProfileLegacyPaymentConnectorTmp.PaymentConnectorName;
        RetailPaymentConnectors retailPaymentConnectors = RetailPaymentConnectors::findByName(connectorName);

        // If this is not multiconnector format validation.
        if (retailPaymentConnectors.RecId && !retailPaymentConnectors.IsMultiConnectorFormat)
        {
            xmlProperties = legacyConnectorSetup.getPropertyValuesAsXmlString(connectorName);
        }
        else
        {
            xmlProperties = element.getEditedMerchantPropertiesAsXml(RetailHardwareProfile.profileId, true);
        }
    }
    else
    {
        // Generating XMl with combined properties for further validation
        xmlProperties = element.getEditedMerchantPropertiesAsXml(RetailHardwareProfile.profileId);
    }
    return strLen(xmlProperties) > maxConnectorPropertiesXmlLength ? checkFailed("@Retail:RetailHardwarePaymentConnectorPropertiesXmlLengthMessage") : true;
}

private void setKeepConnectionOpenFieldEnability()
{
    boolean printerKeepConnectionOpenEnable = retailHardwareProfile.printer == RetailPeripheralType::OPOS;
    PrinterBehavior_printerKeepConnectionOpen.enabled(printerKeepConnectionOpenEnable);

    boolean printer2KeepConnectionOpenEnable = retailHardwareProfile.printer2 == RetailPeripheralType::OPOS;
    Printer2Behavior_printer2KeepConnectionOpen.enabled(printer2KeepConnectionOpenEnable);

    boolean lineDisplayKeepConnectionOpenEnable = retailHardwareProfile.displayDevice == RetailOposNone::OPOS;
    LineDisplayBehavior_lineDisplayKeepConnectionOpen.enabled(lineDisplayKeepConnectionOpenEnable);

    boolean drawerKeepConnectionOpenEnable = retailHardwareProfile.drawer == RetailDrawer::OPOS;
    DrawerBehavior_drawerKeepConnectionOpen.enabled(drawerKeepConnectionOpenEnable);

    boolean drawer2KeepConnectionOpenEnable = retailHardwareProfile.drawer2 == RetailDrawer::OPOS;
    Drawer2Behavior_drawer2KeepConnectionOpen.enabled(drawer2KeepConnectionOpenEnable);
