public int active()
{
    int isActive = super();

    if (!this.hasDecryptionError())
    {
        element.loadConnectorDataToUI();
    }

    if(RetailUtilities::isRetailCDXBackwardCompatibilityEnabled())
    {
        element.loadLegacyConnectorDataToUI();
    }

    // Table RetailHardwareProfile has a new field self-service merged reference package.
    // Call the method below in case the new field does not have any value, but the old field does
    // since now we're showing the new field on this UI and make the legacy field invisible.
    // new field (visible): RetailHardwareProfile.MergedSelfServicePackageReference
    // legacy field (hidden): RetailHardwareProfile.SelfServicePackage
    RetailSelfServiceMigrationHelper::populateMergedPackageReferenceFromSelfServicePackageReference(
        this.cursor(),
        fieldNum(RetailHardwareProfile, SelfServicePackage),
        fieldNum(RetailHardwareProfile, MergedSelfServicePackageReference));

    element.setKeepConnectionOpenFieldEnability();

    return isActive;
}

