    // in the EFTConnectorProperties field, we'll flush the cache.
    CreditCard::clearMerchantAccountPropertiesCache();
}

private boolean hasDecryptionError()
{
    boolean decryptionExceptionCaught = false;
    RetailHardwareProfile hwProfile = this.cursor();

    try
    {
        hwProfile.secureMerchantPropertiesEdit(false, '');
    }
    catch (Exception::CLRError)
    {
        decryptionExceptionCaught = true;
        System.Exception clrException = RetailUtilities::getLatestClrException();
        str exceptionMessage = clrException.get_Message();
        str stackTrace = clrException.get_StackTrace();

        Global::warning(strFmt("@RetailSelfServiceInstaller:DecryptionErrorOnRetailHwProfile", exceptionMessage));

        logger.EventWriteSelfServiceUpdateVirtualPeripheralFailed(
            hwProfile.profileId, exceptionMessage, stackTrace);
    }

    return decryptionExceptionCaught;
}

