/// <summary>
/// validate profile id input
/// </summary>
/// <returns>True if the profile id validation pass; otherwise, false.</returns>
public boolean validate()
{
    boolean ret;

    ret = super();
    System.Text.RegularExpressions.Match regExMatch;

    // regular expression that checks a string must contain at least an alphabet or a number.
    regExMatch = System.Text.RegularExpressions.Regex::Match(retailHardwareProfile.profileId, @'(?=.*\d)|(?=.*[a-z])|(?=.*[A-Z])');
    if(regExMatch.get_Success())
    {

        // regular expression matches an alphanumeric string with specific characters (allow hyphens, parentheses, brackets, curly brackets and underscore)
        regExMatch = System.Text.RegularExpressions.Regex::Match(retailHardwareProfile.profileId, @'^[a-zA-Z0-9-_{\}\]\[\(\)]+$');
        if(regExMatch.get_Success())
        {
            ret = true;
        }
        else
        {
            ret = checkFailed("@RETAIL:HardwareProfileIdSpecialCharValidation");
        }
    }
    else
    {
        ret = checkFailed("@RETAIL:HardwareProfileIdAlphaNumericValidation");
    }
    return ret;
}

public void write()
{
    if(RetailHardwareProfile.EFT == RetailExternalNone::PaymentSDK)
    {
        Map mapSequence = new Map(Types::AnyType,Types::Integer);
        integer lowestSequence;
        boolean isFirstIteration = true;
        MapEnumerator sequenceEnumerator;
        RetailHardwareProfilePaymentConnectorTmp defaultPaymentConnectorTmp;

        // Save serialized properties
        str connectorName = RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorName;
        str connectorProperties = connectorSetup.getPropertyValuesAsXmlString(connectorName);
        RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorProperties = connectorProperties;

        super();
        str merchantProperties = element.getMerchantPropertiesAsXml(RetailHardwareProfile.profileId);
        RetailHardwareProfile.secureMerchantPropertiesEdit(true, merchantProperties);
        // Set EFTConnector to the least sequence from the connector list
        for (defaultPaymentConnectorTmp = RetailHardwareProfilePaymentConnectorTmp_ds.getFirst();
        defaultPaymentConnectorTmp;
        defaultPaymentConnectorTmp = RetailHardwareProfilePaymentConnectorTmp_ds.getNext())
        {
            mapSequence.insert(defaultPaymentConnectorTmp.PaymentConnectorName, defaultPaymentConnectorTmp.PaymentConnectorSequence);
            if(isFirstIteration)
            {
                lowestSequence = defaultPaymentConnectorTmp.PaymentConnectorSequence;
                isFirstIteration = false;
            }
            else
            {
                lowestSequence = min(defaultPaymentConnectorTmp.PaymentConnectorSequence, lowestSequence);
            }
        }

        sequenceEnumerator = mapSequence.getEnumerator();
        while(sequenceEnumerator.moveNext())
        {
            if(sequenceEnumerator.currentValue() == lowestSequence)
            {
                defaultPaymentConnectorTmp.PaymentConnectorName = sequenceEnumerator.currentkey();
                break;
            }
        }

        RetailHardwareProfile.EFTConnectorName = defaultPaymentConnectorTmp.PaymentConnectorName;

        element.ExportDOPSSettings();

        // Since changes might have been made to Merchant Account properties
        // in the SecureMerchantProperties field, we'll flush the cache.
        CreditCard::clearMerchantAccountPropertiesCache();

        boolean isMerchantPropertyUpdated = isInitialLoading ? false : true;
        isInitialLoading = false;

        if (isMerchantPropertyUpdated)
        {
            logger.EventWriteRetailPaymentsMerchantPropertiesUpdated(curUserId(), DateTimeUtil::getSystemDateTime());
        }
    }
}

/// <summary>
/// Initialize the payment connector.
/// </summary>
public void initValue()
{
    super();

    if(RetailHardwareProfile.EFT == RetailExternalNone::PaymentSDK)
    {
        // Read data from Payment properties
        RetailHardwareProfilePaymentConnectorTmp.HardwareProfileId = RetailHardwareProfile.profileId;
        RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorSequence = connectorSequence;

        // Increment Connector Sequence
        connectorSequence++;
    }

}

/// <summary>
/// Validatation on write operation.
/// 1.Merchant properties XML with combined connectors properties not exceeded max allowed for secure string
/// 2.Validate connectors properties by predefined constraints
/// </summary>
/// <returns>True if passed all checks, otherwise false</returns>
public boolean validateWrite()
{
    boolean isValid = super();

    if (isValid
        && (RetailHardwareProfile.EFT == RetailExternalNone::PaymentSDK)
        && (RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorName != ''))
    {
        isValid = element.merchantPropertiesCombinedXMLValidation();
        if(isValid)
        {
            isValid= this.validateConnectorProperties();
            if (connectorSetup.validatePropertyValues(RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorName).displayErrors())
            {
                isValid = isValid && (Box::yesNo("@RET:Retail_Invalid_Payment_Setup", DialogButton::No) == DialogButton::Yes); // The payment setup that you entered is not valid. Do you want to continue?
            }
        }
    }
    return isValid;
}

