/// <summary>
/// Validate the connector properties.
/// </summary>
/// <returns>True if validation passes; Else false.</returns>
private boolean validateConnectorProperties()
{
    boolean result = true;
    RetailHardwareProfilePaymentConnectorTmp hardwareProfilePaymentConnectorTmp;
    hardwareProfilePaymentConnectorTmp.linkPhysicalTableInstance(RetailHardwareProfilePaymentConnectorTmp);

    // Validation 1: Validate that two connectors with the same name do not support the same tender type.
    // Extract the payment method for the  current connector.
    str connectorName = RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorName;
    str connectorProperties = connectorSetup.getPropertyValuesAsXmlString(connectorName);
    System.String[] tenderTypesSupportedByCurrentConnector = this.getSupportedTenderTypes(connectorProperties);

    while select hardwareProfilePaymentConnectorTmp
        where hardwareProfilePaymentConnectorTmp.HardwareProfileId == RetailHardwareProfilePaymentConnectorTmp.HardwareProfileId
        && hardwareProfilePaymentConnectorTmp.PaymentConnectorName == RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorName
        && hardwareProfilePaymentConnectorTmp.RecId != RetailHardwareProfilePaymentConnectorTmp.RecId
    {
        System.String[] tenderTypesSupportedByExistingConnector = this.getSupportedTenderTypes(hardwareProfilePaymentConnectorTmp.PaymentConnectorProperties);

        for(int arrayIndex = 0; arrayIndex < tenderTypesSupportedByCurrentConnector.Length; arrayIndex++)
        {
            // If an existing connector of the same name already supports the tender types being supported by the current connector; Fail validation.
            if (System.Array::IndexOf(tenderTypesSupportedByExistingConnector, tenderTypesSupportedByCurrentConnector.GetValue(arrayIndex)) >= 0)
            {
                result = checkFailed(strFmt("@Retail:TenderTypeSupportedByMultipleConnectorsWarningMessage", tenderTypesSupportedByCurrentConnector.GetValue(arrayIndex), RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorName));
                break;
            }
        }
    }

    return result;
}

/// <summary>
/// Get the supported Tender Types for a given connector.
/// </summary>
/// <param name = "connectorXmlString">The connector properties as an xml string.</param>
/// <returns>The supported tender types.</returns>
private System.String[] getSupportedTenderTypes(str connectorXmlString)
{
    System.String[] result;

    CreditCardPaymentProperties connectorProperties = CreditCardPaymentProperties::fromXmlString(connectorXmlString);
    CreditCardPaymentProperty supportedTenderTypesProperty = connectorProperties.find(PaymentSDK.Constants.GenericNamespace::get_MerchantAccount(), PaymentSDK.Constants.MerchantAccountProperties::get_SupportedTenderTypes());

    if (supportedTenderTypesProperty)
    {
        System.String supportedTenderTypesAsString = supportedTenderTypesProperty.parmStringValue();
        supportedTenderTypesAsString = supportedTenderTypesAsString.ToLowerInvariant();
        result = supportedTenderTypesAsString.Split(';');
    }

    return result;
}

/// <summary>
/// When the payment connector name is modified get the coresponding payment properties of the new or updated connector.
/// </summary>
public void modified()
{
    super();

    // If the connector name is EMPTY string; Set the PaymentConnectorProperties to EMPTY.
    if (System.String::IsNullOrWhiteSpace(RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorName))
    {
        RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorProperties = '';
    }
    else if (RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorName == RetailHardwareProfilePaymentConnectorTmp.orig().PaymentConnectorName)
    {
        // If the connector name is restored to the original value in the table buffer; Restore the PaymentConnectorProperties from the original buffer.
        RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorProperties = RetailHardwareProfilePaymentConnectorTmp.orig().PaymentConnectorProperties;
    }
    else
    {
        // If connector name is modified; Retrieve the default PaymentConnectorProperties for that connector from the PaymentSDK.
        str connectorPropertyXmlStr = connectorSetup.getPropertiesAsXmlString(RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorName);
        RetailHardwareProfilePaymentConnectorTmp.PaymentConnectorProperties = connectorPropertyXmlStr;
    }
    RetailHardwareProfilePaymentConnectorTmp_ds.forceWrite(true);
    element.displaySelectedConnectorProperties();
}

