{
    super();

    // Changes to the grid elements do not register till Save button is clicked. Hence we are force writing on any changes.
    RetailHardwareProfilePaymentConnectorTmp_ds.forceWrite(true);
}

public void write()
{
    if(RetailHardwareProfile.EFT == RetailExternalNone::PaymentSDK)
    {
        // Save serialized properties
        str connectorName = RetailHardwareProfileLegacyPaymentConnectorTmp.PaymentConnectorName;
        str connectorProperties = legacyConnectorSetup.getPropertyValuesAsXmlString(connectorName);
        RetailHardwareProfileLegacyPaymentConnectorTmp.PaymentConnectorProperties = connectorProperties;

        super();

        RetailPaymentConnectors retailPaymentConnectors = RetailPaymentConnectors::findByName(connectorName);
        if (retailPaymentConnectors.RecId && !retailPaymentConnectors.IsMultiConnectorFormat)
        {
            RetailHardwareProfile.EFTConnectorProperties = element.getConnectorPropertiesInSingleConnectorFormat(RetailHardwareProfile.profileId, RetailHardwareProfileLegacyPaymentConnectorTmp);
        }
        else
        {
            RetailHardwareProfile.EFTConnectorProperties = element.getLegacyMerchantPropertiesAsXml(RetailHardwareProfile.profileId);
        }

        // Set LegacyEFTConnectorName to the first connector name on the list of legacy connectors.
        RetailHardwareProfilePaymentConnectorTmp defaultLegacyPaymentConnectorTmp = RetailHardwareProfileLegacyPaymentConnectorTmp_ds.getFirst();
        RetailHardwareProfile.LegacyEFTConnectorName = defaultLegacyPaymentConnectorTmp.PaymentConnectorName;
    }
}

