public void lookup(FormControl _formControl, str _filterStr)
{
    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource = query.addDataSource(tableNum(RetailFiscalIntegrationTechnicalProfile));
    queryBuildDataSource.addRange(fieldNum(RetailFiscalIntegrationTechnicalProfile, ConnectorType)).value(queryValue(RetailFiscalIntegrationConnectorType::Local));
    queryBuildDataSource.addRange(fieldNum(RetailFiscalIntegrationTechnicalProfile, ConnectorLocation)).value(queryValue(RetailFiscalIntegrationConnectorLocation::HardwareStation));
    SysTableLookup lookup = SysTableLookup::newParameters(tableNum(RetailFiscalIntegrationTechnicalProfile), _formControl, true);
    lookup.parmQuery(query);

    // Add the fields to be shown in the lookup form.
    lookup.addLookupfield(fieldNum(RetailFiscalIntegrationTechnicalProfile, ProfileId));
    lookup.addLookupfield(fieldNum(RetailFiscalIntegrationTechnicalProfile, Description));

    lookup.performFormLookup();
}

