void clicked()
{
    CreditCardConnectorName importedConnectorName;
    try
    {
        FileUploadResultBase result = File::GetFileFromUser(classstr(RetailPaymentConnectorMetadataUploadStorageStrategy));
        if (result && result.getUploadStatus())
        {
            System.IO.Stream stream = result.openResult();
            System.IO.MemoryStream memoryStream = new System.IO.MemoryStream();
            System.IO.StreamReader streamReader;
            int64 size = stream.get_Length();
            str content;

            memoryStream.SetLength(size);
            stream.Read(memoryStream.GetBuffer(), 0, size);
            streamReader = new System.IO.StreamReader(memoryStream);
            content = streamReader.ReadToEnd();

            importedConnectorName = RetailPaymentConnectors::importPaymentConnectorMetadata(content);
            info("@Retail_HF_3937382:PaymentConnectorMetadataImportSuccess");
        }
    }
    catch
    {
        throw Global::error("@Retail_HF_3937382:PaymentConnectorImportError");
    }
}

