/// <summary>
/// If there is already a legacy connector selected, check whether it is in single-connector format.
/// If true, then don't allow User to add more legacy connectors.
/// </summary>
public void clicked()
{
    // Refresh the grid's underlying data source so we can check the most up-to-date grid selection.
    RetailHardwareProfileLegacyPaymentConnectorTmp_ds.research(true);

    // Look at the first connector selected at the moment and check whether it is in single-connector format.
    RetailHardwareProfilePaymentConnectorTmp legacyPaymentConnectorTmp;
    legacyPaymentConnectorTmp = getFirstSelection(RetailHardwareProfileLegacyPaymentConnectorTmp_ds);

    if (legacyPaymentConnectorTmp.RecId)
    {
        // Find the connector in the legacy connector table (RetailPaymentConnectors)
        RetailPaymentConnectors retailPaymentConnectors;
        select firstonly1 retailPaymentConnectors where retailPaymentConnectors.Name == legacyPaymentConnectorTmp.PaymentConnectorName;

        // If the connector is single-connector, do not allow normal 'Add' behavior.
        if (retailPaymentConnectors.RecId && !retailPaymentConnectors.IsMultiConnectorFormat)
        {
            // "The legacy system does not support multi-connector functionality. To add a new connector, first remove the existing one."
            warning("@Retail:RetailSingleConnectorAddWarningMessage");
        }
    }
    else
    {
        // No single-connector format was selected, so allow normal 'Add' flow.
        super();
    }
}

/// <summary>
/// Display the list of name of all available connectors for performing a connector name lookup.
/// </summary>
public void lookup()
{
    RetailPaymentConnectorLookupTempTable::performConnectorNameLookup(retailLegacyPaymentConnectorLookupTempTable, this);
}
