/// <summary>
/// Inserts the kit configuration variants records into table <c>tmpRetailKitOrderTable</c> for assembly order processing.
/// </summary>
public void loadKitVariantLines()
{
    boolean isPosted;
    boolean isSalesOrderMarked = RetailKitAssemblyOrder::isSalesOrderMarked(retailKitOrder);
    RetailKitOrderJournal kitOrderJournal;
    EcoResDistinctProductVariant    currentEcoResDistinctProductVariant;
    RecId   ecoResProductMasterRecId;
    InventJournalTable inventJournalTable;
    ecoResProductMasterRecId = RetailKitOrder::findRetailKitByRetailKitOrder(retailKitOrder).ProductMaster;

    changeCompany (retailKitOrder.InventDataAreaId)
    {
        tmpRetailKitOrderTable.setTmpdata(null);

        while select RecId, SearchName from currentEcoResDistinctProductVariant
            where currentEcoResDistinctProductVariant.ProductMaster == ecoResProductMasterRecId
                outer join Quantity from kitOrderJournal
                    where kitOrderJournal.RetailKitOrder == retailKitOrder.RecId
                       && kitOrderJournal.EcoResDistinctProductVariantID == currentEcoResDistinctProductVariant.RecId
                outer join Posted from inventJournalTable
                    where inventJournalTable.JournalId == kitOrderJournal.JournalId
                       && inventJournalTable.dataAreaId == kitOrderJournal.InventDataAreaId
        {
            tmpRetailKitOrderTable.clear();
            tmpRetailKitOrderTable.EcoResDistinctProductVariantID = currentEcoResDistinctProductVariant.RecId;
            tmpRetailKitOrderTable.SearchName    = currentEcoResDistinctProductVariant.SearchName;
            tmpRetailKitOrderTable.Quantity      = abs(kitOrderJournal.Quantity);
            tmpRetailKitOrderTable.AssembledToDate = tmpRetailKitOrderTable.Quantity;
            tmpRetailKitOrderTable.insert();
            isPosted = isPosted || inventJournalTable.Posted;
        }
    }
    retailKitOrder_ds.allowDelete(!isPosted && !isSalesOrderMarked);
    tmpRetailKitOrderTable_ds.allowEdit(!isPosted && !isSalesOrderMarked);
}

NumberSeqFormHandler numberSeqAssemblyOrderName()
{
    if (!numberSeqAssemblyOrderName)
        numberSeqAssemblyOrderName = NumberSeqFormHandler::newForm(RetailParameters::numRefAssemblyOrderName().NumberSequenceId,
                                                             element,
                                                             retailKitOrder_DS,
                                                             fieldNum(RetailKitOrder, Name)
                                                            );
    return numberSeqAssemblyOrderName;
}

public void updateTotalQty()
{
    RetailKitOrder.Quantity = abs((select sum(Quantity)
        from RetailKitOrderJournal
        where RetailKitOrderJournal.RetailKitOrder == retailKitOrder.RecId).Quantity);
    RetailKitOrder.update();
    RetailKitOrder_ds.refresh();
}

public int task(int _taskId)
{
    #Task

    int ret;

    if (_taskId == #taskEditRecord)
    {
        AssembleKitButton.enabled(false);
        DisassembleKitButton.enabled(false);
        shouldSwitchToEditMode = true;
        Post.enabled(false);
    }
    else if (_taskId == #taskDoneEditRecord)
    {
        shouldSwitchToEditMode = false;
        element.resetButtons();
        Post.enabled(true);
    }
    else if (_taskId != #taskSave)
    {
        shouldSwitchToEditMode = false;
    }

    ret = super(_taskId);

    return ret;
}

/// <summary>
/// Resets the button on the form based on the current kit defination.
/// </summary>
/// <remarks>
/// This method should not be called by external callers.
/// </remarks>
public void resetButtons()
{
    RetailKitOrderStatus kitOrderStatus;

    select Posted from kitOrderStatus where kitOrderStatus.RetailKitOrder == RetailKitOrder.RecId;

    if (!shouldSwitchToEditMode)
    {
        AssembleKitButton.enabled(RetailKitOrder.OrderType == RetailKitOrderType::AssemblyOrder && !kitOrderStatus.Posted);
        DisassembleKitButton.enabled(RetailKitOrder.OrderType == RetailKitOrderType::DisassemblyOrder && !kitOrderStatus.Posted);
    }
    deleteButton.enabled(!kitOrderStatus.Posted);
    Post.enabled(!kitOrderStatus.Posted);
}

public void create(boolean _append = false)
{
    element.numberSeqAssemblyOrderName().formMethodDataSourceCreatePre();
    super(_append);
    element.numberSeqAssemblyOrderName().formMethodDataSourceCreate();

    element.loadKitVariantLines();
    tmpRetailKitOrderTable_ds.research();
}

public void linkActive()
{
    super();

    if (element.args().record() is RetailKitOrder)
    {
        element.loadKitVariantLines();
        retailKitOrder_ds.allowCreate(false);
    }
}

/// <summary>
/// select change event for form's primary datasource
/// </summary>
public void selectionChanged()
{
    element.resetButtons();

    super();

    // To restrict multiple calling of loadKitVariantLines(), when RetailKitAssembleKit is opened in create new record mode
    if (RetailKitOrder.RecId)
    {
        element.loadKitVariantLines();
        tmpRetailKitOrderTable_ds.executeQuery();
    }

    QtyToProcess.label("@Retail:RetailReportQuantity");
    QtyProcessedToDate.visible(false);
}

/// <summary>
/// Write method for RetailKitOrder data source method
/// </summary>
public void write()
{
    Query query;
    QueryBuildDataSource qbds;
    QueryBuildRange qbr;

    super();

    query = new Query();
    qbds = query.addDataSource(tablenum(RetailKitOrder));
    qbr = qbds.addRange(fieldnum(RetailKitOrder, Name));
    this.query(query);

    this.research(true);
}

public void lookup(FormControl _formControl, str _filterStr)
