    RetailKitOrder.warehouseLookup(_formControl);
}

public void init()
{
    QueryBuildDataSource qbds;
    super();

    qbds = this.query().dataSourceTable(this.table());
    qbds.clearDynalinks();
    qbds.addDynalink(fieldNum(RetailKitVariantComponent, KitVariant), tmpRetailKitOrderTable, fieldNum(tmpRetailKitOrderTable, EcoResDistinctProductVariantID));
}

