public boolean validateWrite()
{
    return true;
}

public void init()
{
    super();
    EcoResProductTranslation::queryAddDefaultLanguage(this.query());
}

public void write()
{
    ttsBegin;

    if (tmpRetailKitOrderTable.Quantity != tmpRetailKitOrderTable.orig().Quantity)
    {
        RetailKitAssemblyOrder::processOrder(retailKitOrder, tmpRetailKitOrderTable.EcoResDistinctProductVariantID, tmpRetailKitOrderTable.Quantity);
        element.updateTotalQty();
    }

    super();

    ttsCommit;
}

public void init()
{
    super();

    this.query().dataSourceTable(this.table()).addDynalink(
        fieldNum(RetailKitOrderJournal, EcoResDistinctProductVariantId),
        tmpRetailKitOrderTable,
        fieldNum(tmpRetailKitOrderTable, EcoResDistinctProductVariantId));

    this.query().dataSourceTable(this.table()).addDynalink(
        fieldNum(RetailKitOrderJournal, RetailKitOrder),
        retailKitOrder,
        fieldNum(RetailKitOrder, RecId));
}

/// <summary>
/// Click event action for diassemble button.
/// </summary>
public void clicked()
{
    #Task
    element.SwitchToDetailViewMode();

    QtyProcessedToDate.label("@REX4040020");    // Quantity to assemble
    QtyToProcess.label("@REX4040021");          // Assembled

    QtyProcessedToDate.visible(true);
    element.task(#taskEditRecord);
    Post.visible(true);
    Post.enabled(true);
}

/// <summary>
/// Click event action for diassemble button.
/// </summary>
public void clicked()
{
    element.SwitchToDetailViewMode();

    QtyProcessedToDate.label("@REX4160028");    // Quantity to disassemble
    QtyToProcess.label("@REX4160029");          // Disassembled

    QtyProcessedToDate.visible(true);
    Post.visible(true);
    Post.enabled(true);
}

void clicked()
{
    RetailKitOrderStatus kitOrderStatus;

    RetailKitAssemblyOrder::post(RetailKitOrder);

    // After posting, refresh the data source so that the order statuses are updated.
    RetailKitOrder_ds.reread();

    select Posted from kitOrderStatus where kitOrderStatus.RetailKitOrder == RetailKitOrder.RecId;

    // Verify posting for RetailKitOrder and switch to gridview mode
    if (kitOrderStatus.Posted)
    {
        element.SwitchToGridViewMode();
        AssembleKitButton.enabled(false);
        DisassembleKitButton.enabled(false);
        deleteButton.enabled(false);
    }
}
