[Form]
public class RetailLoyaltyCardRewardPointTrans extends FormRun
{
    Args args;
    FormRun callerForm;
    CustTable customerTable;
    RetailAffiliation retailAffiliationTable;
    RetailLoyaltyCard retailLoyaltyCardTable;

}

public void init()
{
    QueryBuildDataSource qbdsRewardPointTrans;

    callerForm = element.args().caller();
    args = element.args();

    super();

    qbdsRewardPointTrans = retailLoyaltyCardRewardPointTrans_ds.query().dataSourceTable(tableNum(RetailLoyaltyCardRewardPointTrans));

    switch (callerForm.name())
    {
        case formStr(CustTable):
            customerTable = args.record() as CustTable;
            qbdsRewardPointTrans
                .addRange(fieldNum(RetailLoyaltyCardRewardPointTrans,CustAccount))
                .value(customerTable.AccountNum);
            break;
        case formStr(RetailLoyaltyPrograms):
            element.design().caption("@REX4520017");
            retailAffiliationTable =  args.record() as RetailAffiliation;
            qbdsRewardPointTrans
                .addRange(fieldNum(RetailLoyaltyCardRewardPointTrans,Affiliation))
                .value(int642str(retailAffiliationTable.RecId));
            break;
        case formStr(RetailLoyaltyCards):
            element.design().caption("@REX4520017");
            retailLoyaltyCardTable =  args.record() as RetailLoyaltyCard;
            qbdsRewardPointTrans
                .addRange(fieldNum(RetailLoyaltyCardRewardPointTrans,CardNumber))
                .value(retailLoyaltyCardTable.CardNumber);
            break;
        default:
            break;
    }

    // Sort by entry date and time
    qbdsRewardPointTrans.addOrderByField(fieldNum(RetailLoyaltyCardRewardPointTrans, EntryDate));
    qbdsRewardPointTrans.addOrderByField(fieldNum(RetailLoyaltyCardRewardPointTrans, EntryTime));

}

display String50 transactionId(RetailLoyaltyCardRewardPointTrans _trans)
{
    String50 transactionId;
    switch (_trans.LoyaltyTransactionType)
    {
        case RetailLoyaltyTransactionType::RetailTransaction:
            transactionId = _trans.transactionId;
            break;

        case RetailLoyaltyTransactionType::SalesOrder:
            transactionId = _trans.SalesId;
            break;

        case RetailLoyaltyTransactionType::LoyaltyAdjustment:
            transactionId = _trans.AdjustmentId;
            break;

        case RetailLoyaltyTransactionType::NonTransactionalActivity:
            transactionId = _trans.NonTransactionalActivityId;
            break;
    }

    return transactionId;
}

public display LineNumOptional transactionLineNumber(RetailLoyaltyCardRewardPointTrans _trans)
{
    LineNumOptional lineNumber;
    switch (_trans.LoyaltyTransactionType)
    {
        case RetailLoyaltyTransactionType::RetailTransaction:
            lineNumber = _trans.RetailTransactionSalesTransLineNum;
            break;

        case RetailLoyaltyTransactionType::SalesOrder:
            lineNumber = _trans.SalesLineLineNum;
            break;

        case RetailLoyaltyTransactionType::LoyaltyAdjustment:
            lineNumber = _trans.RetailTransactionSalesTransLineNum;
            break;
    }

    return lineNumber;
}

/// <summary>
///  Given a loyalty card transaction record, the method returns the local start date of the transaction as per the channel's time zone.
/// </summary>
/// <param name = "_trans"> Loyalty card reward point transaction record. </param>
/// <returns>The local date of transaction based on channel.</returns>
public display date startDateLocal(RetailLoyaltyCardRewardPointTrans _trans)
{
    utcdatetime dateTimeUTC = DateTimeUtil::newDateTime(_trans.StartDate,_trans.EntryTime);
    date localDate;
    if (_trans.Channel == 0)
    {
        localDate =  DateTimeUtil::date(DateTimeUtil::applyTimeZoneOffset(dateTimeUTC, DateTimeUtil::getUserPreferredTimeZone()));
    }
    else
    {
        localDate = DateTimeUtil::date(DateTimeUtil::applyTimeZoneOffset(dateTimeUTC, RetailChannelTable::getChannelTimezone(_trans.Channel)));
    }

    return localDate;
}

/// <summary>
/// When transaction id is clicked, it navigates to the corresponding Retail Transactions/ Sales order / Adjustment form based on
/// the transaction type.
/// </summary>
public void jumpRef()
{
    Args args_mf = new Args();
    MenuFunction mf;

    switch (RetailLoyaltyCardRewardPointTrans.LoyaltyTransactionType)
    {
        case RetailLoyaltyTransactionType::RetailTransaction:
            mf = new MenuFunction(menuitemDisplayStr(RetailTransactionTable), MenuItemType::Display);
            RetailTransactionTable retail_record = RetailTransactionTable::find(RetailLoyaltyCardRewardPointTrans.transactionId, RetailLoyaltyCardRewardPointTrans.storeId, RetailLoyaltyCardRewardPointTrans.terminalId);
            args_mf.record(retail_record);
            args_mf.lookupRecord(retail_record);
            mf.run(args_mf);
            break;

        case RetailLoyaltyTransactionType::SalesOrder:
            mf = new MenuFunction(menuitemDisplayStr(SalesTable), MenuItemType::Display);
            SalesTable salesorder_record = SalesTable::find(RetailLoyaltyCardRewardPointTrans.SalesId);
            args_mf.record(salesorder_record);
            args_mf.lookupRecord(salesorder_record);
            mf.run(args_mf);
            break;

        case RetailLoyaltyTransactionType::LoyaltyAdjustment:
            mf = new MenuFunction(menuitemDisplayStr(RetailLoyaltyCardAdjustment), MenuItemType::Display);
            RetailLoyaltyCardRewardPointAdjustment adj_record = RetailLoyaltyCardRewardPointAdjustment::findByAdjustmentId(RetailLoyaltyCardRewardPointTrans.AdjustmentId);
            args_mf.record(adj_record);
            mf.run(args_mf);
            break;

        case RetailLoyaltyTransactionType::NonTransactionalActivity:
            mf = new MenuFunction(menuitemDisplayStr(RetailLoyaltyCardRewardPointNonTransactionalActivity), MenuItemType::Display);
            RetailLoyaltyCardRewardPointNonTransactionalActivity NonTransactionalActivity_record = RetailLoyaltyCardRewardPointNonTransactionalActivity::findByNonTransactionalActivityId(RetailLoyaltyCardRewardPointTrans.NonTransactionalActivityId);
            args_mf.record(NonTransactionalActivity_record);
            mf.run(args_mf);
            break;
    }

}

/// <summary>
/// Opens the target form EcoResProductInformationDialog.
/// </summary>
public void jumpRef()
{
    InventTable  invetTable;
    super();

    invetTable = InventTable::find(this.valueStr());
    args.record(invetTable);
    EcoResProductInformation::main(args);
}
