public void init()
{
    QueryBuildDataSource qbdsRewardPoint, qbdsSchemeRewardPoint, qbdsScheme, qbdsLoyaltyGroup, qbdsCardTier;

    super();

    // Add data sources to RetailLoyaltyRewardPoint in order to filter reward points by loyalty card
    qbdsRewardPoint = RetailLoyaltyRewardPoint_ds.query().dataSourceTable(tableNum(RetailLoyaltyRewardPoint));

    qbdsSchemeRewardPoint = qbdsRewardPoint.addDataSource(TableNum(RetailLoyaltySchemeRewardPointView), tableStr(RetailLoyaltySchemeRewardPointView));
    qbdsSchemeRewardPoint.joinMode(JoinMode::ExistsJoin);
    qbdsSchemeRewardPoint.addLink(fieldNum(RetailLoyaltyRewardPoint, RecId), fieldNum(RetailLoyaltySchemeRewardPointView, RewardPoint));

    qbdsScheme = qbdsSchemeRewardPoint.addDataSource(TableNum(RetailLoyaltyScheme), tableStr(RetailLoyaltyScheme));
    qbdsScheme.joinMode(JoinMode::ExistsJoin);
    qbdsScheme.addLink(fieldNum(RetailLoyaltySchemeRewardPointView, LoyaltyScheme), fieldNum(RetailLoyaltyScheme, RecId));

    qbdsLoyaltyGroup = qbdsScheme.addDataSource(TableNum(RetailAffiliation), tableStr(RetailAffiliation));
    qbdsLoyaltyGroup.joinMode(JoinMode::ExistsJoin);
    qbdsLoyaltyGroup.addLink(fieldNum(RetailLoyaltyScheme, Affiliation), fieldNum(RetailAffiliation, RecId));

    qbdsCardTier = qbdsLoyaltyGroup.addDataSource(TableNum(RetailLoyaltyCardTier), tableStr(RetailLoyaltyCardTier));
    qbdsCardTier.joinMode(JoinMode::ExistsJoin);
    qbdsCardTier.addLink(fieldNum(RetailAffiliation, RecId), fieldNum(RetailLoyaltyCardTier, Affiliation));
}

NumberSeqFormHandler numberSeqLoyaltyCardNumber()
{
    if (!numberSeqLoyaltyCustNumber)
        numberSeqLoyaltyCustNumber = numberSeqFormHandler::newForm(RetailParameters::numRefLoyaltyCardNumber().NumberSequenceId,
                                                             element,
                                                             RetailLoyaltyCard_DS,
                                                             fieldNum(RetailLoyaltyCard, CardNumber)
                                                            );
    return numberSeqLoyaltyCustNumber;
}

public void executeQuery()
{
    //retrieve only customer's loyalty cards if called from customer service form or all customers form
    if (element.args().caller())
    {
        QueryBuildDataSource        qbds;
        str callingForm=  element.args().caller().name();
        qbds = this.query().dataSourceTable(tableNum(RetailLoyaltyCard));

        if (callingForm == formStr(MCRCustomerService) || callingForm == formStr(CustTable))
        {
            QueryBuildDataSource qbdsCustTable = this.query().dataSourceTable(tableNum(CustTable));
            CustTable custRecord = element.args().record();

            if (!qbdsCustTable)
            {
                qbdsCustTable = qbds.addDataSource(tableNum(CustTable));
                qbdsCustTable.addRange(fieldNum(CustTable,AccountNum)).value(custRecord.AccountNum);
                qbdsCustTable.addLink(fieldNum(RetailLoyaltyCard, Party), fieldNum(CustTable, Party));
                qbdsCustTable.joinMode(JoinMode::ExistsJoin);
            }
            else
            {
                // Find the range for a specific field
                QueryBuildRange qbdsCustTableRange;

                qbdsCustTableRange = qbdsCustTable.findRange(fieldNum(CustTable, AccountNum));
                qbdsCustTableRange.value(custRecord.AccountNum);
            }
        }
        else if (callingForm == formStr(RetailChannelLoyaltyCards))
        {
            RetailLoyaltyCard retailLoyaltyCardRecord = element.args().record();

            qbds.clearDynalinks();
            qbds.addRange(fieldNum(RetailLoyaltyCard, CardNumber)).value(retailLoyaltyCardRecord.CardNumber);
        }
    } 

    super();
}

