public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (ret && !element.numberSeqLoyaltyCardNumber().formMethodDataSourceValidateWrite())
    {
        ret = false;
    }

    if (ret && RetailLoyaltyCard.CardTenderType == RetailLoyaltyTenderTypeBase::Blocked)
    {
        if(RetailLoyaltyCard.LoyaltyBlockedDate == DateTimeUtil::date(DateTimeUtil::minValue()))
        {
            RetailLoyaltyCard.LoyaltyBlockedDate = DateTimeUtil::date(DateTimeUtil::utcNow());
        }

        // When card is blocked check if all points associated should be removed
        if (Box::yesNo("@Retail:SetLoyaltyRewardPointsToZero", DialogButton::No, '') == DialogButton::Yes)
        {
            RetailLoyaltyCard rtc;

            select firstonly rtc
                where rtc.CardNumber == RetailLoyaltyCard.CardNumber;

            RetailLoyaltyCard::SetLoyaltyPointsToZero(rtc);
        }
    }
    else
    {
        RetailLoyaltyCard.LoyaltyBlockedDate = DateTimeUtil::date(DateTimeUtil::minValue());
    }

    return ret;
}

