void clicked()
{
    RetailAffiliation affiliation;
    RetailLoyaltyCardTier cardTier;
    DialogButton result;
    // N-1 Mode check
    if (isConfigurationkeyEnabled(configurationKeyNum(RetailCDXBackwardCompatibility)))
    {
        select count(RecId) from affiliation
            join Affiliation, LoyaltyCard from cardTier
                where affiliation.RecId == cardTier.Affiliation
                    && cardTier.LoyaltyCard == RetailLoyaltyCard.RecId;

        // Only need to show the message when the SECOND loyalty group is added the loyalty card
        if (affiliation.RecId == 1)
        {
            result = Box::yesNo("@REX4161342", DialogButton::No);
            if (result == DialogButton::No)
            {
                return;
            }
        }
    }
    super();
}

