public void close()
{
    if (numberSeqOperatingUnitNumber)
    {
        numberSeqOperatingUnitNumber.formMethodClose();
    }

    super();
}

NumberSeqFormHandler numberSeqOperatingUnitNumber()
{
    if (!numberSeqOperatingUnitNumber)
    {
        numberSeqOperatingUnitNumber = NumberSeqFormHandler::newForm(
            OMOperatingUnit::getNumberSequenceReference().NumberSequenceId,
            element,
            OMOperatingUnit_DS,
            fieldNum(OMOperatingUnit, OMOperatingUnitNumber));
    }
    return numberSeqOperatingUnitNumber;
}

NumberSeqFormHandler numberSeqRetailChannelId()
{
    if (!numberSeqRetailChannelId)
{
    numberSeqRetailChannelId = numberSeqFormHandler::newForm(RetailParameters::numRefRetailChannelId().NumberSequenceId,
                                                         element,
                                                         RetailMCRChannelTable_DS,
                                                         fieldNum(RetailMCRChannelTable,RetailChannelId)
                                                         );
}

    return numberSeqRetailChannelId;
}

public void init()
{
    super();

    legalEntity.text(curext());
    selectedDataAreaRecId = CompanyInfo::findDataArea(legalEntity.text()).RecId;

    DimensionEntryControl.parmCompany(legalEntity.text());

    RetailMCRChannelTable_ds.object(fieldNum(RetailMCRChannelTable, CalcExemptTaxesForPriceInclusive)).visible(true);

    this.setPriceGroupsButtonVisibility();
}

private void setPriceGroupsButtonVisibility()
{
    PriceGroupsButton.visible(!RetailPricingFeatureToggle::isRetailPricingPropertyFeatureEnabled());
}

boolean ValidateInfoCode(RetailInfocodeId infocodeId, str reasonCode)
{
    boolean ret = true;

    if (!RetailInfocodeTable::exist(infocodeId)
        || RetailInfocodeTable::find(infocodeId).InputType != RetailInfocodeInputType::SubCode)
    {
        ret =  checkFailed(strFmt("@SYS94968", infocodeId, reasonCode));
    }

    return ret;
}

private void InfocodeLookup(FormControl callingControl)
{
    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource;
    QueryBuildRange queryBuildRange;

    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(RetailInfocodeLookup), callingControl);
    sysTableLookup.addLookupField(fieldNum(RetailInfocodeLookup, InfoCodeId));
    sysTableLookup.addLookupField(fieldNum(RetailInfocodeLookup, Description));

    queryBuildDataSource = query.addDataSource(tableNum(RetailInfocodeLookup));

    queryBuildRange = queryBuildDataSource.addRange(fieldNum(RetailInfocodeLookup, InputType));
    queryBuildRange.value(enum2str(RetailInfocodeInputType::SubCode));

    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();
}

