public int active()
{
    #Task
    int ret;

    ret = super();

    element.design().caption(strFmt("@MCR4410354", OMOperatingUnit.Name));

    element.hideControlsForRetailTrial();

    RetailMCRChannelTable_DS.object(fieldNum(RetailMCRChannelTable, MCREnableOrderCompletion)).visible(!RetailCommercePaymentsFeatureExposure::isEnabled());

    RetailChannelIdFormControlHelper::setRetailChannelIdControlEditableStatus(RetailMCRChannelTable_RetailChannelId, RetailMCRChannelTable.RecId == 0);

    return ret;
}

public void write()
{
    ttsbegin;

    // Ensure that RetailOnlineChannelTable and OMOperatingUnit are updated transactionally.
    oMOperatingUnit.write();

    RetailMCRChannelTable.OMOperatingUnitID = OMOperatingUnit.RecId;
    super();
