void enableFieldActive()
{
    retailParameters_ds.object(fieldnum(RetailParameters, LedgerAccountType)).allowEdit(retailParameters.PostPeriodicDisc ? true : false);
    retailParameters_ds.object(fieldnum(RetailParameters, DiscountAccountLedgerDimension)).allowEdit(retailParameters.LedgerAccountType == RetailBookingLedgerAccountTypeBase::Periodic && retailParameters.PostPeriodicDisc ? true : false);
    retailParameters_ds.object(fieldnum(RetailParameters, MixMatchLedgerDimension)).allowEdit(retailParameters.LedgerAccountType == RetailBookingLedgerAccountTypeBase::Periodic && retailParameters.PostPeriodicDisc ? true : false);
    retailParameters_ds.object(fieldnum(RetailParameters, MultiBuyLedgerDimension)).allowEdit(retailParameters.LedgerAccountType == RetailBookingLedgerAccountTypeBase::Periodic && retailParameters.PostPeriodicDisc ? true : false);
    retailParameters_ds.object(fieldnum(RetailParameters, OfferLedgerDimension)).allowEdit(retailParameters.LedgerAccountType == RetailBookingLedgerAccountTypeBase::Periodic && retailParameters.PostPeriodicDisc ? true : false);
    retailParameters_ds.object(fieldnum(RetailParameters, ThresholdLedgerDimension)).allowEdit(retailParameters.LedgerAccountType == RetailBookingLedgerAccountTypeBase::Periodic && retailParameters.PostPeriodicDisc ? true : false);
    retailParameters_ds.object(fieldnum(RetailParameters, SkipAggregationForReturns)).allowEdit(retailParameters.aggregateBeforePosting);
    RetailParameters_ds.object(fieldnum(RetailParameters, EnableDelayedPriceAndDiscountCalculation)).enabled(RetailPricingCustomizableFeatureToggle::isPreventUnintendedRecalculationEnabled());

    BestDiscountConfirmChkBox.enabled(retailParameters.DiscountAlgorithmMode == RetailDiscountAlgorithmMode::Exhaustive ? true : false);

    // <GIN>
    this.enableFieldActive_IN();
    // </GIN>
    // <GEERU>
    this.enableFieldActive_RU();
    // </GEERU>

    this.enableFieldActiveDelegate(this);
}

/// <summary>
/// Delegate to allow enabling or disabling addition UI controls and data source fields.
/// </summary>
/// <param name = "formRunObject">The form object.</param>
delegate void enableFieldActiveDelegate(xFormRun formRunObject)
{
}

/// <summary>
/// Enables/disables India retail parameters.
/// </summary>
/// <remarks>
/// 1) The "Default transfer type" parameter is enabled when the inventory parameter
///   "Stock transfer" is activated.
/// 2) The "Default price type" parameter is enabled when the "Default transfer type"
///    parameter is set to "Stock transfer".
/// </remarks>
void enableFieldActive_IN()
{
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        retailParameters_ds.object(fieldNum(RetailParameters, DefaultTransferType_IN)).allowEdit(true);

        retailParameters_ds.object(fieldNum(RetailParameters, DefaultPriceType_IN)).allowEdit(
            retailParameters.DefaultTransferType_IN == TransferType_IN::StockTransfer ? true : false);
    }
}

/// <summary>
/// Enables/disables RU retail parameters.
/// </summary>
void enableFieldActive_RU()
{
    if (SysCountryRegionCode::isLegalEntityInCountryRegion(#easternEuropeAllandRU))
    {
        retailParameters_ds.object(fieldNum(RetailParameters, AggregateSalesReturns_RU)).allowEdit(retailParameters.AggregateBeforePosting && !retailParameters.SkipAggregationForReturns);
        retailParameters_ds.object(fieldnum(RetailParameters, GiftCardClosingLedgerDimension)).allowEdit(!retailParameters.ProcessGiftCardsAsPrepayments_RU);
    }
}

