public void init()
{
    this.numberSeqPreInit();

    super();

    ReturnDispositionCodeId.mandatory(!RetailUnifiedReturnsFeatureExposure::isEnabled());

    RetailParameters::find();
    RetailParametersEx1::find();
    RetailClientelingParameters::find();


    if (RetailChronologicalInvoicePostingReasonCodeSetter::isEnabled())
    {
        RetailChronologicalInvoicePostingParameters::find();

        ChronologicalInvoicePosting.visible(true);
    }

    OrderSearchTabPage.visible(RetailAnonymousUserSearchOrderFeatureExposure::isEnabled());

    str documentationText = strFmt("<a target='_blank' href='https://go.microsoft.com/fwlink/?linkid=2111488'>%1</a>", "@Retail:RetailFraudProtectionDocumentationLink");
    str dfpDescriptionPart1Text = strFmt("@Retail:RetailFraudProtectionDescriptionText", documentationText);
    str privacyText = strFmt("<a target='_blank' href='https://go.microsoft.com/fwlink/?LinkId=521839'>%1</a>", "@Retail:RetailFraudProtectionPrivacyLink");
    str dfpDescrpitionPart2Text = strFmt("@Retail:RetailFraudProtectionDescriptionTextPart2", privacyText);
    FraudProtectionLegalNoticeHtmlView.parmHtml(strFmt("<html><body>%1</body></html>", dfpDescriptionPart1Text + dfpDescrpitionPart2Text));
    RetailFraudProtectionParameters::find();

    TextBestPerfInfo.text(strFmt("%1 : %2","@RetailPricing:OverlapDiscountsHandleMarginal","@RetailPricing:OverlapDiscountsHandleMarginalInfo"));
    TextBalancedInfo.text(strFmt("%1 : %2","@RetailPricing:OverlapDiscountsHandleBalanced","@RetailPricing:OverlapDiscountsHandleBalancedInfo"));
    TextBestDiscount.text(strFmt("%1 : %2","@RetailPricing:OverlapDiscountsHandleExhaustive","@RetailPricing:OverlapDiscountsHandleExhaustiveInfo"));

    container cortanaAnalyticsAppConfiguration = RetailTransactionServiceRecommendations::GetCortanaAnalyticsAppConfiguration(RetailTransactionServiceRecommendations::CortanaAnalyticsApplicationName);
    if (!conPeek(cortanaAnalyticsAppConfiguration, 1))
    {
        element.design().controlName("MachineLearning").visible(false);
    }

    if (!RetailParameters::isRetailAutoChargesEnabledAndInUse())
    {
        element.design().controlName("RetailParameters_ChargeOverrideReasonCode").visible(false);
    }

    TmpRetailTransactionValidationRule.linkPhysicalTableInstance(TmpRetailTransactionValidationRule::populateTmpTable());

    RetailSharedParameters sharedParameters = RetailSharedParameters::find();

    if (sharedParameters && sharedParameters.ProductAvailabilityJobPolicy == RetailProductAvailabilityJobControlModel::PopulateRetailProductWarehouseInventoryTable)
    {
        // hide order fulfillment location setting if optimized product availability job is set
        element.design().controlName("EcommerceTabPage").visible(false);
    }

    if (!RetailSMB::IsRetailSMBEnabled())
    {
        InitializeDefaultCOA.visible(false);
    }

    // 'Check for package updates' button should be visible if it is NOT an on-premise environments.
    var environment = Microsoft.Dynamics.ApplicationPlatform.Environment.EnvironmentFactory::GetApplicationEnvironment();
    boolean isOnPremiseEnv = environment.Common.HostingEnvironment ==
        Microsoft.Dynamics.ApplicationPlatform.Environment.HostingEnvironment::OnPremises;
    CheckForUpdatesButtonGroup.visible(!isOnPremiseEnv);

    boolean isMultiplePickupFeatureEnabled = FeatureStateProvider::isFeatureEnabled(RetailMultiplePickupDeliveryModeFeature::instance());
    DeliveryModePage.visible(isMultiplePickupFeatureEnabled);
    GroupDeliveryMode.visible(!isMultiplePickupFeatureEnabled);

    RetailParameters_ds.object(fieldNum(RetailParameters, InventoryAvailabilityQuantityReturnType)).visible(true);

    boolean unifiedReturnsEnabled = RetailUnifiedReturnsFeatureExposure::isEnabled();
    RetailParameters_AllowExchangeOnReturnOrders.visible(!unifiedReturnsEnabled);
    StoreInventory_EnableValidationOfSerialNumbersOnPosReturns.visible(unifiedReturnsEnabled);

    SalesAgreementPage.visible(FeatureStateProvider::isFeatureEnabled(RetailSalesAgreementPriceSupportFeature::instance()));

    // Set visibility of 'B2B Customers' Group based on whether B2B ECommerce feature is enabled.
    B2bCustomersGroup.visible(FeatureStateProvider::isFeatureEnabled(RetailB2BEcommerceFeature::instance()));

    // Only shows 'Omni-channel media management' if the feature is enabled.
    OmniChannelMediaManagementTabPage.visible(FeatureStateProvider::isFeatureEnabled(OmniChannelMediaManagementAuthoringFeature::instance()));

    // Set Commerce Validator tab visibility
    RetailCommerceValidatorParameters validatorParameters = RetailCommerceValidatorParameters::find();
    ValidatorPage.visible(validatorParameters.ShowRetailParameters);
}

void numberSeqPreInit()
{
    runExecuteDirect   = false;
    // Initialize the numberSequenceModules to an empty container.
    numberSequenceModules = conNull();

    EventHandlerResult result = new EventHandlerResult();
    this.addNumberSeqModulesDelegate(result);

    if (result.hasResult())
    {
        // Retrieve any number sequence module areas added by the delegate.
        numberSequenceModules = result.result();
    }

    // Retail has 4 NumberSequenceModules in total
    // Each module may contain DataArea scope number sequences
    numberSequenceModules += [NumberSeqModule::RetailParameters, NumberSeqModule::RetailEx1, NumberSeqModule::RetailParametersEx2, NumberSeqModule::RBOStore];
    scope = NumberSeqScopeFactory::createDataAreaScope();

    NumberSeqApplicationModule::createReferencesMulti(numberSequenceModules, scope);
    tmpIdRef.setTmpData(NumberSequenceReference::configurationKeyTableMulti(numberSequenceModules));
}

/// <summary>
/// Delegate to load additional number sequence module areas.
/// </summary>
/// <param name = "_result">Subscriber should use this object to return the additional number sequence module areas.</param>
/// <remarks>The subscriber should return a container holding additional number sequence module areas.</remarks>
delegate void addNumberSeqModulesDelegate(EventHandlerResult _result)
{
}

/// <summary>
/// Modify  a queryrange to only show records in the ui for the specific company being navigated by the user.
/// </summary>
/// <param name = "dataSource">The query data source.</param>
/// <param name = "tableIdentifier">The table identifier.</param>
/// <param name = "packageType">Self-service package type.</param>
/// <param name = "tableReferenceId">The table reference identifier.</param>
void setRetailSelfServiceDefaultPackageQuery(QueryBuildDataSource dataSource, TableId tableIdentifier, PackageType packageType, RefRecId tableReferenceId = 0)
{
    RetailSelfServiceDefaultPackages defaultPackage = RetailSelfServiceDefaultPackages::findDefaultPackage(
        tableIdentifier, packageType, tableReferenceId);

    dataSource.addRange(fieldNum(RetailSelfServiceDefaultPackages, TableIdentifier)).value(int2Str(tableIdentifier));
    dataSource.addRange(fieldNum(RetailSelfServiceDefaultPackages, TableReferenceId)).value(int642Str(tableReferenceId));
    dataSource.addRange(fieldNum(RetailSelfServiceDefaultPackages, MergedSelfServicePackageReference)).value(int642Str(defaultPackage.MergedSelfServicePackageReference));
}

