/// <summary>
/// Enables change tracking on a specific data entity.
/// </summary>
/// <param name = "targetEntity">The target entity.</param>
/// <param name = "changeTrackingType">The change tracking type.</param>
private void enableEntityChangeTracking(str targetEntity, DMFChangeTrackingType changeTrackingType)
{
    if (changeTrackingType == DMFChangeTrackingType::None)
    {
        // Do nothing if the change tracking type is None.
        return;
    }

    DMFEntity dmfEntity;
    select firstonly EntityName from dmfEntity
        where dmfEntity.TargetEntity == targetEntity;

    boolean skipIfAlreadyEnabled = true;
    DMFEntityBase::enableChangeTrackingOnEntity(dmfEntity.EntityName, changeTrackingType, skipIfAlreadyEnabled);
}

