/// <summary>
/// Validate the form data before saving it to the data source.
/// </summary>
/// <returns></returns>
public boolean validateWrite()
{
    boolean ret;
    List custAttrErrorMessages;
    ListEnumerator custAttrErrorMessagesEnumerator;

    ret = super();

    // Customer attribute group
    if (retailParameters.CustomerAttributeGroup > 0)
    {
        custAttrErrorMessages = RetailCustomerAttributeHelper::validateCustAttrGroupOnRetailParameters(retailParameters.CustomerAttributeGroup);

        // Give log warning if attribute group contains unsupported attribute types.
        custAttrErrorMessagesEnumerator = custAttrErrorMessages.getEnumerator();

        // Value is true when no error messages found.
        ret = ret && custAttrErrorMessages.elements() == 0;

        while (custAttrErrorMessagesEnumerator.moveNext())
        {
            checkFailed(custAttrErrorMessagesEnumerator.current());
        }
    }

    return ret;
}

[SysObsolete('The method is deprecated.', false, 05\09\2022)]
public edit RetailDiscountAlgorithmMode editDiscountAlgorithmMode(
    boolean set, RetailParameters _retailParameters, RetailDiscountAlgorithmMode _discountAlgorithmMode)
{
    if(set)
    {
        boolean isToBeSetAsBestDiscount = _discountAlgorithmMode == RetailDiscountAlgorithmMode::Exhaustive;

        if (_discountAlgorithmMode != RetailDiscountAlgorithmMode::Marginal && RetailParameters.AllowThresholdJoinExclusiveCompetition)
        {
            boolean ok = Box::okCancel("@RetailDiscount:ChangeAlgorithmWarning", DialogButton::Cancel) == DialogButton::Ok;

            if (ok)
            {
                RetailParameters.AllowThresholdJoinExclusiveCompetition = NoYes::No;
            }
            else
            {
                // user cancels the change.
                return _retailParameters.DiscountAlgorithmMode;
            }
        }

        _retailParameters.DiscountAlgorithmMode = _discountAlgorithmMode;

        // user change from best discount to other option, reset the check box
        if (BestDiscountConfirmChkBox.checked() && !isToBeSetAsBestDiscount)
        {
            BestDiscountConfirmChkBox.checked(false);
        }
        BestDiscountConfirmChkBox.enabled(isToBeSetAsBestDiscount);

        retailParameters_ds.object(fieldnum(RetailParameters, AllowThresholdJoinExclusiveCompetition)).enabled(retailParameters.DiscountAlgorithmMode == RetailDiscountAlgorithmMode::Marginal);
    }

    return _retailParameters.DiscountAlgorithmMode;
}

